lor: #0066cc;\n        font-size: 1.2rem;\n        border-bottom: 1px solid #e0e0e0;\n        padding-bottom: 8px;\n        margin-bottom: 20px;\n      }\n\n      \n\n      .executive-grid[_ngcontent-%COMP%] {\n        display: grid;\n        grid-template-columns: repeat(4, 1fr);\n        gap: 15px;\n        margin-bottom: 20px;\n      }\n\n      .exec-card[_ngcontent-%COMP%] {\n        background: linear-gradient(135deg, #f8f9fa 0%, #e9ecef 100%);\n        padding: 15px;\n        border-radius: 8px;\n        text-align: center;\n        border-left: 4px solid #0066cc;\n      }\n\n      .exec-value[_ngcontent-%COMP%] {\n        font-size: 1.5rem;\n        font-weight: 700;\n        color: #0066cc;\n      }\n\n      .exec-label[_ngcontent-%COMP%] {\n        font-size: 0.8rem;\n        font-weight: 600;\n        color: #333;\n      }\n\n      .exec-desc[_ngcontent-%COMP%] {\n        font-size: 0.7rem;\n        color: #666;\n      }\n\n      .executive-text[_ngcontent-%COMP%] {\n        background: #f8f9fa;\n        padding: 20px;\n        border-radius: 8px;\n        font-size: 0.95rem;\n      }\n\n      \n\n      .problem-grid[_ngcontent-%COMP%] {\n        display: grid;\n        grid-template-columns: repeat(2, 1fr);\n        gap: 15px;\n      }\n\n      .problem-card[_ngcontent-%COMP%] {\n        background: #fff3e0;\n        padding: 20px;\n        border-radius: 8px;\n        border-left: 4px solid #ff9800;\n      }\n\n      .problem-icon[_ngcontent-%COMP%] {\n        font-size: 1.5rem;\n        margin-bottom: 10px;\n      }\n\n      .problem-card[_ngcontent-%COMP%]   h4[_ngcontent-%COMP%] {\n        margin: 0 0 8px;\n        color: #e65100;\n      }\n\n      .problem-card[_ngcontent-%COMP%]   p[_ngcontent-%COMP%] {\n        margin: 0;\n        font-size: 0.85rem;\n      }\n\n      \n\n      .solution-overview[_ngcontent-%COMP%] {\n        background: #e8f5e9;\n        padding: 20px;\n        border-radius: 8px;\n        margin-bottom: 20px;\n      }\n\n      .f