import { Component, OnInit } from "@angular/core";
import {
  UaeDataService,
  UaeFactDto,
  InternationalBenchmarkDto,
} from "../../services/uae-data.service";

interface KeyMetric {
  value: string;
  label: string;
  source: string;
}

interface FactCategory {
  name: string;
  facts: Array<{ text: string; source: string; relevance: number }>;
}

interface BenchmarkView {
  city: string;
  system: string;
  reduction: number;
  revenue: number;
  chargeRange: string;
  lessons: string;
}

interface PolicyView {
  name: string;
  alignment: number;
  target: string;
  contribution: string;
  evidence: string;
}

@Component({
  selector: "app-uae-facts-dashboard",
  template: `
    <div class="uae-facts-container">
      <div class="uae-header">
        <div class="uae-flag">
          <div class="red"></div>
          <div class="green"></div>
          <div class="white"></div>
          <div class="black"></div>
        </div>
        <h2>üá¶üá™ UAE Transport Facts & Context</h2>
      </div>

      <div class="metrics-grid">
        <div class="metric-card" *ngFor="let metric of keyMetrics">
          <div class="metric-value">{{ metric.value }}</div>
          <div class="metric-label">{{ metric.label }}</div>
          <div class="metric-source">{{ metric.source }}</div>
        </div>
      </div>

      <div class="facts-categories">
        <div class="category" *ngFor="let category of categories">
          <h3>{{ category.name }}</h3>
          <div class="fact" *ngFor="let fact of category.facts">
            <div class="fact-text">{{ fact.text }}</div>
            <div class="fact-source">Source: {{ fact.source }}</div>
            <div class="fact-relevance">Relevance: {{ fact.relevance }}/10</div>
          </div>
        </div>
      </div>

      <div class="benchmarks-section">
        <h3>üåç International Congestion Pricing Benchmarks</h3>
        <table class="benchmarks-table">
          <thead>
            <tr>
              <th>City</th>
              <th>System</th>
              <th>Congestion Reduction</th>
              <th>Annual Revenue</th>
              <th>Charge Range</th>
              <th>Lessons for UAE</th>
            </tr>
          </thead>
          <tbody>
            <tr *ngFor="let benchmark of benchmarks">
              <td>{{ benchmark.city }}</td>
              <td>{{ benchmark.system }}</td>
              <td>
                <div class="progress-bar">
                  <div
                    class="progress-fill"
                    [style.width.%]="benchmark.reduction"
                  ></div>
                  <span>{{ benchmark.reduction }}%</span>
                </div>
              </td>
              <td>{{ benchmark.revenue | currency: "USD" }}M</td>
              <td>{{ benchmark.chargeRange }}</td>
              <td class="lessons">{{ benchmark.lessons }}</td>
            </tr>
          </tbody>
        </table>
      </div>

      <div class="policy-alignment">
        <h3>üìú Alignment with UAE Government Policies</h3>
        <div class="policy-card" *ngFor="let policy of policies">
          <div class="policy-header">
            <h4>{{ policy.name }}</h4>
            <span
              class="alignment-badge"
              [ngClass]="getAlignmentClass(policy.alignment)"
            >
              {{ policy.alignment }}% aligned
            </span>
          </div>
          <div class="policy-target">{{ policy.target }}</div>
          <div class="policy-contribution">
            <strong>Our Contribution:</strong> {{ policy.contribution }}
          </div>
          <div class="policy-evidence">{{ policy.evidence }}</div>
        </div>
      </div>

      <div class="economic-calculator">
        <h3>üí∞ UAE-Specific Economic Impact</h3>
        <div class="calculator-inputs">
          <div class="input-group">
            <label>Congestion Reduction (%)</label>
            <input
              type="range"
              [(ngModel)]="congestionReduction"
              min="0"
              max="100"
              step="1"
            />
            <span>{{ congestionReduction }}%</span>
          </div>
          <div class="input-group">
            <label>Daily Vehicles Affected</label>
            <input
              type="number"
              [(ngModel)]="dailyVehicles"
              min="1000"
              max="100000"
              step="1000"
            />
            <span>{{ dailyVehicles | number }} vehicles</span>
          </div>
        </div>

        <div class="calculator-results">
          <div class="result-card">
            <div class="result-label">Productivity Gains</div>
            <div class="result-value">
              {{ calculateProductivity() | currency: "AED" }}
            </div>
            <div class="result-explanation">
              Equivalent to {{ calculateJobs() }} full-time jobs
            </div>
          </div>
          <div class="result-card">
            <div class="result-label">Fuel Savings</div>
            <div class="result-value">
              {{ calculateFuelSavings() | currency: "AED" }}
            </div>
            <div class="result-explanation">
              Enough to power {{ calculateHomes() }} homes for a year
            </div>
          </div>
          <div class="result-card">
            <div class="result-label">CO2 Reduction Value</div>
            <div class="result-value">
              {{ calculateCO2Value() | currency: "AED" }}
            </div>
            <div class="result-explanation">
              Equivalent to planting {{ calculateTrees() }} trees
            </div>
          </div>
          <div class="result-card total">
            <div class="result-label">Total Annual Economic Impact</div>
            <div class="result-value">
              {{ calculateTotalImpact() | currency: "AED" }}
            </div>
            <div class="result-explanation">
              {{ calculateROI() }} return on investment
            </div>
          </div>
        </div>
      </div>
    </div>
  `,
  styles: [
    `
      .uae-flag {
        display: flex;
        height: 30px;
        width: 120px;
        margin-right: 15px;
      }
      .uae-flag .red {
        flex: 1;
        background: #ce1126;
      }
      .uae-flag .green {
        flex: 1;
        background: #00732f;
      }
      .uae-flag .white {
        flex: 1;
        background: white;
      }
      .uae-flag .black {
        flex: 1;
        background: black;
      }

      .metrics-grid {
        display: grid;
        grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
        gap: 15px;
        margin: 20px 0;
      }

      .metric-card {
        background: white;
        border-left: 4px solid #ce1126;
        padding: 15px;
        border-radius: 8px;
        box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
      }

      .progress-bar {
        height: 20px;
        background: #f0f0f0;
        border-radius: 10px;
        position: relative;
        overflow: hidden;
      }

      .progress-fill {
        height: 100%;
        background: linear-gradient(90deg, #00732f, #00a550);
        transition: width 0.3s ease;
      }

      .alignment-badge {
        padding: 4px 12px;
        border-radius: 20px;
        font-size: 12px;
        font-weight: bold;
      }

      .alignment-badge.high {
        background: #00732f;
        color: white;
      }
      .alignment-badge.medium {
        background: #ffc107;
        color: black;
      }
      .alignment-badge.low {
        background: #dc3545;
        color: white;
      }
    `,
  ],
})
export class UaeFactsDashboardComponent implements OnInit {
  keyMetrics: KeyMetric[] = [];
  categories: FactCategory[] = [];
  benchmarks: BenchmarkView[] = [];
  policies: PolicyView[] = [];

  congestionReduction = 35;
  dailyVehicles = 35000;

  constructor(private uaeDataService: UaeDataService) {}

  ngOnInit(): void {
    this.loadUaeFacts();
    this.loadBenchmarks();
    this.loadPolicies();
  }

  loadUaeFacts(): void {
    this.uaeDataService.getFacts().subscribe((facts) => {
      this.keyMetrics = facts
        .filter((f) => f.relevance >= 9)
        .slice(0, 6)
        .map((fact) => ({
          value: this.extractMetricValue(fact),
          label: fact.category,
          source: fact.source,
        }));

      const categories = Array.from(new Set(facts.map((f) => f.category)));
      this.categories = categories.map((cat) => ({
        name: cat,
        facts: facts
          .filter((f) => f.category === cat)
          .slice(0, 3)
          .map((fact) => ({
            text: fact.fact,
            source: fact.source,
            relevance: fact.relevance,
          })),
      }));
    });
  }

  loadBenchmarks(): void {
    this.uaeDataService.getBenchmarks().subscribe((benchmarks) => {
      this.benchmarks = benchmarks.map((benchmark) => ({
        city: benchmark.city,
        system: benchmark.system,
        reduction: benchmark.congestionReduction,
        revenue: benchmark.annualRevenueMillionUSD,
        chargeRange: benchmark.chargeRange,
        lessons: benchmark.lessons ?? benchmark.advantages ?? "",
      }));
    });
  }

  loadPolicies(): void {
    this.policies = [
      {
        name: "UAE Vision 2030",
        alignment: 92,
        target: "Reduce commute times by 30%",
        contribution: "Projected 35% congestion reduction",
        evidence: "Simulation exceeds national target",
      },
      {
        name: "UAE Net Zero 2050",
        alignment: 85,
        target: "Reduce transport emissions by 40%",
        contribution: "CO2 reduction trajectory on track",
        evidence: "Supported by real-time mobility data",
      },
      {
        name: "Dubai Urban Mobility Strategy",
        alignment: 88,
        target: "Increase public transport share to 30%",
        contribution: "Modal shift improvements expected",
        evidence: "Pricing incentives favor public transport",
      },
    ];
  }

  getAlignmentClass(alignment: number): string {
    if (alignment >= 85) return "high";
    if (alignment >= 60) return "medium";
    return "low";
  }

  calculateProductivity(): number {
    const hoursSavedPerVehicle = (this.congestionReduction / 100) * 0.5;
    const dailyProductivity = this.dailyVehicles * hoursSavedPerVehicle * 150;
    return dailyProductivity * 365;
  }

  calculateFuelSavings(): number {
    const litersSavedPerVehicle = (this.congestionReduction / 100) * 0.5;
    const dailyFuelSavings = this.dailyVehicles * litersSavedPerVehicle * 3.25;
    return dailyFuelSavings * 365;
  }

  calculateCO2Value(): number {
    const litersSavedPerVehicle = (this.congestionReduction / 100) * 0.5;
    const dailyLitersSaved = this.dailyVehicles * litersSavedPerVehicle;
    const kgCo2Saved = dailyLitersSaved * 2.31;
    const tonsCo2Saved = (kgCo2Saved * 365) / 1000;
    return tonsCo2Saved * 250;
  }

  calculateJobs(): number {
    const annualWage = 150 * 8 * 260;
    return Math.max(1, Math.round(this.calculateProductivity() / annualWage));
  }

  calculateHomes(): number {
    return Math.max(1, Math.round(this.calculateFuelSavings() / 3000));
  }

  calculateTrees(): number {
    return Math.max(1, Math.round(this.calculateCO2Value() / 50));
  }

  calculateTotalImpact(): number {
    return (
      this.calculateProductivity() +
      this.calculateFuelSavings() +
      this.calculateCO2Value()
    );
  }

  calculateROI(): string {
    const totalImpact = this.calculateTotalImpact();
    const baseInvestment = 1_000_000;
    const roi = totalImpact / baseInvestment;
    return `${roi.toFixed(1)}x`;
  }

  private extractMetricValue(fact: UaeFactDto): string {
    const numberMatch = fact.fact.match(/([\d.]+\s*(?:billion|million|M)?)/i);
    if (numberMatch) return numberMatch[0];
    return `${fact.year}`;
  }
}
