import { Component, OnInit } from "@angular/core";
import { ZoneService } from "../../services/zone.service";
import { Zone, ZoneUpsertRequest } from "../../models/zone.model";

@Component({
  selector: "app-zone-editor",
  templateUrl: "./zone-editor.component.html",
  styleUrls: ["./zone-editor.component.css"],
})
export class ZoneEditorComponent implements OnInit {
  zones: Zone[] = [];
  selectedZone: Zone | null = null;
  isEditing = false;
  isCreating = false;
  formData: Partial<Zone> = {};
  isLoading = false;
  error: string | null = null;
  success: string | null = null;

  // Time range for peak hours
  peakStartTime: Date = new Date();
  peakEndTime: Date = new Date();

  zoneTypes = ["Residential", "Commercial", "Industrial", "Mixed"];
  emirates = ["Dubai", "Abu Dhabi", "Sharjah"];

  constructor(private zoneService: ZoneService) {}

  ngOnInit(): void {
    this.loadZones();
  }

  loadZones(): void {
    this.isLoading = true;
    this.zoneService.getAllZones().subscribe({
      next: (zones: Zone[]) => {
        this.zones = zones;
        this.isLoading = false;
      },
      error: (err: any) => {
        this.error = "Failed to load zones";
        this.isLoading = false;
      },
    });
  }

  startCreate(): void {
    this.isCreating = true;
    this.isEditing = false;
    this.selectedZone = null;
    this.formData = {
      baseFreeHours: 2,
      chargePerHour: 20,
      peakHours: "",
    };
  }

  editZone(zone: Zone): void {
    this.selectedZone = zone;
    this.isEditing = true;
    this.isCreating = false;
    this.formData = { ...zone };
  }

  saveZone(): void {
    if (
      !this.formData.name ||
      !this.formData.emirate ||
      !this.formData.zoneType
    ) {
      this.error = "Please fill in all required fields";
      return;
    }

    const payload: ZoneUpsertRequest = {
      name: this.formData.name?.trim() ?? "",
      emirate: this.formData.emirate as ZoneUpsertRequest["emirate"],
      zoneType: this.formData.zoneType as ZoneUpsertRequest["zoneType"],
      baseFreeHours: this.formData.baseFreeHours ?? 2,
      peakHours:
        this.formData.peakHours && this.formData.peakHours.trim().length > 0
          ? this.formData.peakHours.trim()
          : "[]",
      chargePerHour: this.formData.chargePerHour ?? 20,
    };

    this.isLoading = true;
    if (this.isCreating) {
      this.zoneService.createZone(payload).subscribe({
        next: (newZone: Zone) => {
          this.zones.push(newZone);
          this.success = "Zone created successfully";
          this.resetForm();
          this.isLoading = false;
        },
        error: (err: any) => {
          this.error = "Failed to create zone";
          this.isLoading = false;
        },
      });
    } else if (this.selectedZone) {
      this.zoneService.updateZone(this.selectedZone.id, payload).subscribe({
        next: (updatedZone: Zone) => {
          const index = this.zones.findIndex((z) => z.id === updatedZone.id);
          if (index > -1) {
            this.zones[index] = updatedZone;
          }
          this.success = "Zone updated successfully";
          this.resetForm();
          this.isLoading = false;
        },
        error: (err: any) => {
          this.error = "Failed to update zone";
          this.isLoading = false;
        },
      });
    }
  }

  deleteZone(zoneId: string): void {
    if (!confirm("Are you sure you want to delete this zone?")) return;

    this.isLoading = true;
    this.zoneService.deleteZone(zoneId).subscribe({
      next: () => {
        this.zones = this.zones.filter((z) => z.id !== zoneId);
        this.success = "Zone deleted successfully";
        this.resetForm();
        this.isLoading = false;
      },
      error: (err: any) => {
        this.error = "Failed to delete zone";
        this.isLoading = false;
      },
    });
  }

  get groupedZones(): { emirate: string; zones: Zone[] }[] {
    const groups = new Map<string, Zone[]>();
    for (const zone of this.zones) {
      const key = zone.emirate || "Unknown";
      if (!groups.has(key)) {
        groups.set(key, []);
      }
      groups.get(key)?.push(zone);
    }

    const orderedEmirates = this.emirates.filter((e) => groups.has(e));
    const otherEmirates = Array.from(groups.keys())
      .filter((e) => !orderedEmirates.includes(e))
      .sort((a, b) => a.localeCompare(b));

    const emirateOrder = [...orderedEmirates, ...otherEmirates];
    return emirateOrder.map((emirate) => ({
      emirate,
      zones: (groups.get(emirate) ?? []).sort((a, b) =>
        a.name.localeCompare(b.name),
      ),
    }));
  }

  resetForm(): void {
    this.isEditing = false;
    this.isCreating = false;
    this.selectedZone = null;
    this.formData = {};
    this.error = null;
    setTimeout(() => (this.success = null), 3000);
  }

  cancel(): void {
    this.resetForm();
  }
}
<div class="editor-container">
  <!-- Header -->
  <div class="page-header">
    <div class="header-content">
      <div class="header-icon">🗺️</div>
      <div>
        <h1>Zone Editor</h1>
        <p class="subtitle">Manage smart zone configurations across the UAE</p>
      </div>
    </div>
    <button
      (click)="startCreate()"
      *ngIf="!isEditing && !isCreating"
      class="btn-primary btn-large"
    >
      <span class="btn-icon">+</span> Create New Zone
    </button>
  </div>

  <!-- Alerts -->
  <div *ngIf="success" class="alert alert-success">
    <span class="alert-icon">✓</span>
    <span>{{ success }}</span>
  </div>
  <div *ngIf="error" class="alert alert-error">
    <span class="alert-icon">⚠️</span>
    <span>{{ error }}</span>
  </div>

  <!-- Form Panel -->
  <div *ngIf="isEditing || isCreating" class="form-panel">
    <div class="panel-header">
      <h3>{{ isCreating ? "Create New Zone" : "Edit Zone" }}</h3>
      <button (click)="cancel()" class="btn-close">✕</button>
    </div>

    <form (ngSubmit)="saveZone()" class="zone-form">
      <div class="form-section">
        <h4>Basic Information</h4>
        <div class="form-row">
          <div class="form-group">
            <label>Zone Name *</label>
            <input
              type="text"
              [(ngModel)]="formData.name"
              name="name"
              required
              placeholder="e.g., Deira Business District"
            />
          </div>
        </div>

        <div class="form-row three-cols">
          <div class="form-group">
            <label>Emirate *</label>
            <select [(ngModel)]="formData.emirate" name="emirate" required>
              <option value="">Select Emirate</option>
              <option *ngFor="let e of emirates" [value]="e">{{ e }}</option>
            </select>
          </div>

          <div class="form-group">
            <label>Zone Type *</label>
            <select [(ngModel)]="formData.zoneType" name="zoneType" required>
              <option value="">Select Type</option>
              <option *ngFor="let t of zoneTypes" [value]="t">{{ t }}</option>
            </select>
          </div>

          <div class="form-group">
            <label>Charge Per Hour (AED)</label>
            <input
              type="number"
              [(ngModel)]="formData.chargePerHour"
              name="chargePerHour"
              min="0"
              step="0.01"
              placeholder="20.00"
            />
          </div>
        </div>
      </div>

      <div class="form-section">
        <h4>Pricing Configuration</h4>
        <div class="form-row three-cols">
          <div class="form-group">
            <label>Base Free Hours</label>
            <div class="input-with-unit">
              <input
                type="number"
                [(ngModel)]="formData.baseFreeHours"
                name="baseFreeHours"
                min="0"
                max="24"
              />
              <span class="unit">hrs</span>
            </div>
          </div>

          <div class="form-group full-width">
            <label>Peak Hours (optional)</label>
            <div class="time-range-picker">
              <app-date-time-picker
                [label]="'Start'"
                [showTime]="true"
                [showQuickActions]="false"
                [(ngModel)]="peakStartTime"
                name="peakStartTime"
              ></app-date-time-picker>
              <span class="time-separator">to</span>
              <app-date-time-picker
                [label]="'End'"
                [showTime]="true"
                [showQuickActions]="false"
                [(ngModel)]="peakEndTime"
                name="peakEndTime"
              ></app-date-time-picker>
            </div>
            <span class="input-hint">Set the peak pricing hours</span>
          </div>
        </div>
      </div>

      <div class="form-actions">
        <button type="submit" class="btn-primary" [disabled]="isLoading">
          <span *ngIf="!isLoading">{{
            isCreating ? "✓ Create Zone" : "✓ Update Zone"
          }}</span>
          <span *ngIf="isLoading">⏳ Saving...</span>
        </button>
        <button type="button" (click)="cancel()" class="btn-secondary">
          Cancel
        </button>
      </div>
    </form>
  </div>

  <!-- Zones Table -->
  <div class="zones-section">
    <div class="section-header">
      <h2>Existing Zones</h2>
      <span class="zone-count">{{ zones.length }} zones configured</span>
    </div>

    <div class="table-container">
      <table class="modern-table">
        <thead>
          <tr>
            <th>Zone Name</th>
            <th>Emirate</th>
            <th>Type</th>
            <th>Free Hours</th>
            <th>Rate</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody *ngFor="let group of groupedZones">
          <tr class="group-header">
            <td colspan="6">
              <span class="emirate-badge" [attr.data-emirate]="group.emirate">
                {{ group.emirate }}
              </span>
              <span class="zone-count-badge"
                >{{ group.zones.length }} zones</span
              >
            </td>
          </tr>
          <tr *ngFor="let zone of group.zones" class="zone-row">
            <td class="zone-name">{{ zone.name }}</td>
            <td>
              <span class="emirate-tag">{{ zone.emirate }}</span>
            </td>
            <td>
              <span class="type-badge" [attr.data-type]="zone.zoneType">
                {{ zone.zoneType }}
              </span>
            </td>
            <td>
              <span class="free-hours">
                <span class="hours-value">{{ zone.baseFreeHours }}</span>
                <span class="hours-unit">hrs</span>
              </span>
            </td>
            <td>
              <span class="rate-value">AED {{ zone.chargePerHour }}/hr</span>
            </td>
            <td>
              <div class="action-buttons">
                <button
                  (click)="editZone(zone)"
                  class="btn-action btn-edit"
                  title="Edit"
                >
                  ✎
                </button>
                <button
                  (click)="deleteZone(zone.id)"
                  class="btn-action btn-delete"
                  title="Delete"
                >
                  🗑️
                </button>
              </div>
            </td>
          </tr>
        </tbody>
      </table>

      <div *ngIf="zones.length === 0" class="empty-state">
        <div class="empty-icon">🗺️</div>
        <h3>No zones configured yet</h3>
        <p>Create your first smart zone to get started</p>
        <button (click)="startCreate()" class="btn-primary">
          + Create First Zone
        </button>
      </div>
    </div>
  </div>
</div>
