import { Component, OnInit } from "@angular/core";
import { RTADataService } from "../../services/rta-data.service";
import { VehicleMovement, CitizenImpact } from "../../models/simulation.model";

@Component({
  selector: "app-citizen-impact",
  templateUrl: "./citizen-impact.component.html",
  styleUrls: ["./citizen-impact.component.css"],
})
export class CitizenImpactComponent implements OnInit {
  citizenImpact: CitizenImpact | null = null;
  selectedScenario: "current" | "pessimistic" | "optimistic" = "current";
  incomeBracket: string = "Medium";
  vehicleType: string = "Private";
  homeZone: string = "Dubai Marina";
  workZone: string = "Deira Business";

  scenarios: Array<{
    name: string;
    value: "current" | "pessimistic" | "optimistic";
  }> = [
    { name: "Current Proposal", value: "current" },
    { name: "Pessimistic (High Rates)", value: "pessimistic" },
    { name: "Optimistic (Fair Rates)", value: "optimistic" },
  ];

  incomeBrackets = ["Low", "Medium", "High"];
  vehicleTypes = ["Private", "Taxi", "Commercial"];

  isLoading = false;
  error: string | null = null;

  constructor(private rtaService: RTADataService) {}

  ngOnInit(): void {
    this.calculateImpact();
  }

  selectScenario(scenario: "current" | "pessimistic" | "optimistic"): void {
    this.selectedScenario = scenario;
    this.calculateImpact();
  }

  calculateImpact(): void {
    this.isLoading = true;
    this.error = null;

    // This would call the backend to calculate personalized impact
    // For now, we'll simulate it
    setTimeout(() => {
      this.citizenImpact = {
        vehicleOwner: {
          id: "USER123",
          incomeBracket: this.incomeBracket,
          homeZone: this.homeZone,
          workZone: this.workZone,
          vehicleType: this.vehicleType,
        },
        weeklyImpact: {
          estimatedCharges: this.calculateCharges(),
          chargePercentageOfIncome: this.calculatePercentage(),
          alternativeRoutes: this.getAlternativeRoutes(),
          timeSavings: this.getTimeSavings(),
        },
        affectedBy: this.getAffectedZones(),
      };
      this.isLoading = false;
    }, 500);
  }

  calculateCharges(): number {
    const baseRate = 20;
    const dailyTrips = 2;
    const workingDays = 5;
    const avgDuration = 45; // minutes

    let rate = baseRate;
    if (this.selectedScenario === "pessimistic") rate *= 1.5;
    if (this.selectedScenario === "optimistic") rate *= 0.7;

    if (this.vehicleType === "Taxi") return 0; // Exempt

    return (avgDuration / 60) * rate * dailyTrips * workingDays;
  }

  calculatePercentage(): number {
    const charges = this.calculateCharges();
    const monthlyIncome = this.getMonthlyIncome();
    return (charges / monthlyIncome) * 100;
  }

  getMonthlyIncome(): number {
    switch (this.incomeBracket) {
      case "Low":
        return 3000;
      case "Medium":
        return 8000;
      case "High":
        return 20000;
      default:
        return 8000;
    }
  }

  getAlternativeRoutes(): number {
    const routeOptions = this.selectedScenario === "optimistic" ? 3 : 1;
    return routeOptions;
  }

  getTimeSavings(): number {
    if (this.selectedScenario === "current") return 15;
    if (this.selectedScenario === "optimistic") return 25;
    return 5;
  }

  getAffectedZones(): string[] {
    return [this.homeZone, this.workZone];
  }

  getImpactMessage(): string {
    if (!this.citizenImpact) return "";

    const charges = this.citizenImpact.weeklyImpact.estimatedCharges;
    const percentage = this.citizenImpact.weeklyImpact.chargePercentageOfIncome;
    const timeSavings = this.citizenImpact.weeklyImpact.timeSavings;

    if (this.vehicleType === "Taxi") {
      return `As a Taxi driver, you're EXEMPT from congestion charges under the fairness policy.`;
    }

    if (percentage > 2) {
      return `⚠️ Your weekly charges (AED ${charges.toFixed(
        0
      )}) would be ${percentage.toFixed(
        1
      )}% of your income - considered high burden.`;
    }

    if (percentage > 1) {
      return `Your weekly charges would be AED ${charges.toFixed(
        0
      )} (${percentage.toFixed(
        1
      )}% of income). You could save ${timeSavings} minutes daily with route diversions.`;
    }

    return `Your weekly charges would be minimal at AED ${charges.toFixed(
      0
    )} (${percentage.toFixed(1)}% of income).`;
  }

  getFairnessRating(): "Fair" | "Concerning" | "Unfair" {
    const percentage =
      this.citizenImpact?.weeklyImpact.chargePercentageOfIncome || 0;
    if (percentage <= 1) return "Fair";
    if (percentage <= 2) return "Concerning";
    return "Unfair";
  }
}
<div class="impact-container">
  <h2>How Will This Affect ME?</h2>
  <p class="subtitle">Personalized impact assessment for congestion pricing</p>

  <div class="scenario-selector">
    <h3>Compare Scenarios</h3>
    <div class="scenario-buttons">
      <button
        *ngFor="let scenario of scenarios"
        [class.active]="selectedScenario === scenario.value"
        (click)="selectScenario(scenario.value)"
      >
        {{ scenario.name }}
      </button>
    </div>
  </div>

  <div class="personal-details">
    <h3>Your Profile</h3>
    <div class="form-row">
      <div class="form-group">
        <label>Income Bracket</label>
        <select
          [(ngModel)]="incomeBracket"
          (change)="calculateImpact()"
          name="income"
        >
          <option *ngFor="let bracket of incomeBrackets" [value]="bracket">
            {{ bracket }}
          </option>
        </select>
      </div>

      <div class="form-group">
        <label>Vehicle Type</label>
        <select
          [(ngModel)]="vehicleType"
          (change)="calculateImpact()"
          name="vehicle"
        >
          <option *ngFor="let type of vehicleTypes" [value]="type">
            {{ type }}
          </option>
        </select>
      </div>
    </div>

    <div class="form-row">
      <div class="form-group">
        <label>Home Zone</label>
        <select [(ngModel)]="homeZone" (change)="calculateImpact()" name="home">
          <option value="Dubai Marina">Dubai Marina</option>
          <option value="Sharjah Residential">Sharjah Residential</option>
          <option value="Khalifa City">Khalifa City</option>
        </select>
      </div>

      <div class="form-group">
        <label>Work Zone</label>
        <select [(ngModel)]="workZone" (change)="calculateImpact()" name="work">
          <option value="Deira Business">Deira Business</option>
          <option value="Musaffah Industrial">Musaffah Industrial</option>
          <option value="Downtown Dubai">Downtown Dubai</option>
        </select>
      </div>
    </div>
  </div>

  <div *ngIf="citizenImpact" class="impact-results">
    <div
      class="impact-message"
      [class.warning]="getFairnessRating() === 'Concerning'"
      [class.danger]="getFairnessRating() === 'Unfair'"
    >
      {{ getImpactMessage() }}
    </div>

    <div class="metrics-grid">
      <div class="impact-metric">
        <h4>Weekly Charges</h4>
        <p class="big-number">
          AED
          {{ citizenImpact.weeklyImpact.estimatedCharges | number : "1.0-0" }}
        </p>
      </div>
      <div class="impact-metric">
        <h4>% of Weekly Income</h4>
        <p
          class="big-number"
          [class.high]="citizenImpact.weeklyImpact.chargePercentageOfIncome > 2"
        >
          {{
            citizenImpact.weeklyImpact.chargePercentageOfIncome
              | number : "1.2-1"
          }}%
        </p>
      </div>
      <div class="impact-metric">
        <h4>Time Saved Daily</h4>
        <p class="big-number">
          {{ citizenImpact.weeklyImpact.timeSavings }} min
        </p>
      </div>
      <div class="impact-metric">
        <h4>Alternative Routes</h4>
        <p class="big-number">
          {{ citizenImpact.weeklyImpact.alternativeRoutes }}
        </p>
      </div>
    </div>

    <div class="fairness-indicator">
      <h4>
        Fairness Rating:
        <span [class]="getFairnessRating().toLowerCase()">{{
          getFairnessRating()
        }}</span>
      </h4>
      <p *ngIf="getFairnessRating() === 'Fair'">
        ✓ Pricing impact is proportional to your income level
      </p>
      <p *ngIf="getFairnessRating() === 'Concerning'">
        ⚠️ Pricing impact is moderately high for your income bracket
      </p>
      <p *ngIf="getFairnessRating() === 'Unfair'">
        ✗ Pricing creates significant burden for your income bracket - consider
        exemptions or subsidies
      </p>
    </div>

    <div class="comparison-table">
      <h4>Scenario Comparison</h4>
      <table>
        <thead>
          <tr>
            <th>Metric</th>
            <th>Current</th>
            <th>Pessimistic</th>
            <th>Optimistic</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>Weekly Charges</td>
            <td class="current">
              AED {{ calculateCharges() | number : "1.0-0" }}
            </td>
            <td class="pessimistic">
              AED {{ calculateCharges() * 1.5 | number : "1.0-0" }}
            </td>
            <td class="optimistic">
              AED {{ calculateCharges() * 0.7 | number : "1.0-0" }}
            </td>
          </tr>
          <tr>
            <td>% of Income</td>
            <td class="current">
              {{ calculatePercentage() | number : "1.2-1" }}%
            </td>
            <td class="pessimistic">
              {{ calculatePercentage() * 1.5 | number : "1.2-1" }}%
            </td>
            <td class="optimistic">
              {{ calculatePercentage() * 0.7 | number : "1.2-1" }}%
            </td>
          </tr>
        </tbody>
      </table>
    </div>
  </div>

  <div *ngIf="isLoading" class="loading">
    Calculating your personalized impact...
  </div>

  <div *ngIf="error" class="error">
    {{ error }}
  </div>
</div>
