import { Component, OnInit } from "@angular/core";
import { PilotProposalService } from "../../services/pilot-proposal.service";
import { SimulationService } from "../../services/simulation.service";
import { SimulationResult } from "../../models/simulation.model";
import {
  PilotOptions,
  PilotProposalDto,
} from "../../models/pilot-proposal.model";

@Component({
  selector: "app-pilot-proposal",
  templateUrl: "./pilot-proposal.component.html",
  styleUrls: ["./pilot-proposal.component.css"],
})
export class PilotProposalComponent implements OnInit {
  simulations: SimulationResult[] = [];
  selectedSimulationId = "";
  proposal: PilotProposalDto | null = null;
  error: string | null = null;
  isGenerating = false;

  options: PilotOptions = {
    includeUaePassIntegration: false,
    includeMobileApp: false,
    includeHardwareIntegration: false,
    include24x7Support: false,
    durationMonths: 6,
    customIntegrationCost: 0,
  };

  constructor(
    private pilotService: PilotProposalService,
    private simulationService: SimulationService,
  ) {}

  ngOnInit(): void {
    this.loadSimulations();
  }

  loadSimulations(): void {
    this.simulationService.getAllSimulations().subscribe({
      next: (data) => {
        this.simulations = data;
        if (data.length > 0) {
          this.selectedSimulationId = data[0].id;
        }
      },
      error: () => {
        this.error = "Failed to load simulations";
      },
    });
  }

  applyTemplate(template: "quick" | "full" | "enterprise"): void {
    if (template === "quick") {
      this.options = {
        includeUaePassIntegration: false,
        includeMobileApp: false,
        includeHardwareIntegration: false,
        include24x7Support: false,
        durationMonths: 6,
        customIntegrationCost: 0,
      };
    }

    if (template === "full") {
      this.options = {
        includeUaePassIntegration: true,
        includeMobileApp: true,
        includeHardwareIntegration: true,
        include24x7Support: true,
        durationMonths: 6,
        customIntegrationCost: 0,
      };
    }

    if (template === "enterprise") {
      this.options = {
        includeUaePassIntegration: true,
        includeMobileApp: true,
        includeHardwareIntegration: true,
        include24x7Support: true,
        durationMonths: 9,
        customIntegrationCost: 215000,
      };
    }
  }

  generateProposal(): void {
    if (!this.selectedSimulationId) {
      this.error = "Select a simulation first";
      return;
    }

    this.isGenerating = true;
    this.error = null;

    this.pilotService
      .generateProposal(this.selectedSimulationId, this.options)
      .subscribe({
        next: (proposal) => {
          this.proposal = proposal;
          this.isGenerating = false;
        },
        error: () => {
          this.error = "Failed to generate proposal";
          this.isGenerating = false;
        },
      });
  }

  getSelectedSimulation(): SimulationResult | null {
    return (
      this.simulations.find((s) => s.id === this.selectedSimulationId) || null
    );
  }

  getTotalCost(): number {
    let total = 250000;
    if (this.options.includeUaePassIntegration) total += 50000;
    if (this.options.includeMobileApp) total += 75000;
    if (this.options.includeHardwareIntegration) total += 100000;
    if (this.options.include24x7Support) total += 60000;
    if (this.options.customIntegrationCost > 0)
      total += this.options.customIntegrationCost;
    return total;
  }

  getEstimatedAnnualRevenue(): number {
    const sim = this.getSelectedSimulation();
    if (!sim) return 0;
    return sim.estimatedRevenue * 365;
  }

  getEstimatedMonthlyRevenue(): number {
    const sim = this.getSelectedSimulation();
    if (!sim) return 0;
    return (sim.estimatedRevenue * 365) / 12;
  }

  getROI(): number {
    const cost = this.getTotalCost();
    const revenue = this.getEstimatedAnnualRevenue();
    if (cost === 0) return 0;
    return ((revenue - cost) / cost) * 100;
  }

  getRoiPieStyle(): string {
    const roi = Math.max(0, Math.min(100, this.getROI()));
    return `conic-gradient(#4CAF50 ${roi}%, #ECEFF1 ${roi}% 100%)`;
  }

  exportPdf(): void {
    if (!this.proposal?.pdfBase64) return;
    const byteCharacters = atob(this.proposal.pdfBase64);
    const byteNumbers = new Array(byteCharacters.length);
    for (let i = 0; i < byteCharacters.length; i += 1) {
      byteNumbers[i] = byteCharacters.charCodeAt(i);
    }
    const byteArray = new Uint8Array(byteNumbers);
    const blob = new Blob([byteArray], { type: "application/pdf" });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = "pilot-proposal.pdf";
    a.click();
    window.URL.revokeObjectURL(url);
  }

  exportPowerPoint(): void {
    if (!this.proposal) return;
    const html = `
      <html><body>
        <h1>Smart Zone Pilot Proposal</h1>
        <h2>Executive Summary</h2>
        <p>${this.proposal.executiveSummary}</p>
        <h2>Problem Statement</h2>
        <p>${this.proposal.problemStatement}</p>
        <h2>Proposed Solution</h2>
        <p>${this.proposal.proposedSolution}</p>
        <h2>Pilot Details</h2>
        <ul>
          ${this.proposal.pilotDetails.timeline
            .map((item) => `<li>${item}</li>`)
            .join("")}
        </ul>
        <h2>Financials</h2>
        <p>Year 1 Revenue Projection: AED ${this.proposal.financials.year1RevenueProjection.toFixed(0)}</p>
        <p>Year 1 ROI: ${this.proposal.financials.year1ROI.toFixed(1)}%</p>
      </body></html>
    `;
    const blob = new Blob([html], {
      type: "application/vnd.ms-powerpoint",
    });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = "pilot-proposal.ppt";
    a.click();
    window.URL.revokeObjectURL(url);
  }

  copyExecutiveSummary(): void {
    if (!this.proposal?.executiveSummary) return;
    navigator.clipboard.writeText(this.proposal.executiveSummary);
  }
}
<div class="pilot-container">
  <div class="pilot-header">
    <h2>Generate Pilot Proposal</h2>
    <p>Government-ready proposal builder with live ROI and cost insights.</p>
  </div>

  <div class="pilot-grid">
    <div class="pilot-form">
      <h3>Proposal Generator</h3>

      <label>Simulation</label>
      <select [(ngModel)]="selectedSimulationId">
        <option value="">Select simulation</option>
        <option *ngFor="let sim of simulations" [value]="sim.id">
          {{ sim.name }} • AED {{ sim.estimatedRevenue | number }} / day
        </option>
      </select>

      <div class="templates">
        <button (click)="applyTemplate('quick')">Quick Pilot</button>
        <button (click)="applyTemplate('full')">Full Implementation</button>
        <button (click)="applyTemplate('enterprise')">Enterprise</button>
      </div>

      <div class="options">
        <label>
          <input
            type="checkbox"
            [(ngModel)]="options.includeUaePassIntegration"
          />
          Include UAE Pass integration (50,000 AED)
        </label>
        <label>
          <input type="checkbox" [(ngModel)]="options.includeMobileApp" />
          Include mobile application (75,000 AED)
        </label>
        <label>
          <input
            type="checkbox"
            [(ngModel)]="options.includeHardwareIntegration"
          />
          Include Salik gate hardware integration (100,000 AED)
        </label>
        <label>
          <input type="checkbox" [(ngModel)]="options.include24x7Support" />
          Include 24/7 support (60,000 AED)
        </label>
      </div>

      <label>Duration (months)</label>
      <input
        type="range"
        min="3"
        max="12"
        step="1"
        [(ngModel)]="options.durationMonths"
      />
      <div class="duration-value">{{ options.durationMonths }} months</div>

      <label>Custom Integration Cost (AED)</label>
      <input
        type="number"
        [(ngModel)]="options.customIntegrationCost"
        min="0"
        step="5000"
      />

      <button
        class="btn-generate"
        (click)="generateProposal()"
        [disabled]="isGenerating"
      >
        {{ isGenerating ? "Generating..." : "Generate Proposal" }}
      </button>

      <div class="error" *ngIf="error">{{ error }}</div>
    </div>

    <div class="pilot-display">
      <h3>Interactive Proposal</h3>

      <div class="live-calc">
        <div>
          <span>Total Pilot Cost</span>
          <strong>AED {{ getTotalCost() | number }}</strong>
        </div>
        <div>
          <span>Estimated Annual Revenue</span>
          <strong>AED {{ getEstimatedAnnualRevenue() | number }}</strong>
        </div>
        <div>
          <span>Estimated Monthly Revenue</span>
          <strong>AED {{ getEstimatedMonthlyRevenue() | number }}</strong>
        </div>
      </div>

      <div class="roi-card">
        <div class="roi-pie" [style.background]="getRoiPieStyle()"></div>
        <div>
          <h4>ROI</h4>
          <p>{{ getROI() | number: "1.0-1" }}%</p>
        </div>
      </div>

      <div class="timeline">
        <h4>Timeline ({{ options.durationMonths }} months)</h4>
        <div class="timeline-bars">
          <div class="bar design">Design</div>
          <div class="bar integration">Integration</div>
          <div class="bar build">Build & QA</div>
          <div class="bar pilot">Pilot Run</div>
        </div>
      </div>

      <div class="risk-matrix">
        <h4>Risk Matrix</h4>
        <div class="matrix-grid">
          <div class="cell low">Low</div>
          <div class="cell medium">Med</div>
          <div class="cell high">High</div>
          <div class="cell medium">Med</div>
          <div class="cell high">High</div>
          <div class="cell high">High</div>
          <div class="cell low">Low</div>
          <div class="cell medium">Med</div>
          <div class="cell high">High</div>
        </div>
      </div>

      <div class="proposal-preview" *ngIf="proposal">
        <h4>Executive Summary</h4>
        <p>{{ proposal.executiveSummary }}</p>

        <h4>Problem Statement</h4>
        <p>{{ proposal.problemStatement }}</p>

        <h4>Proposed Solution</h4>
        <p>{{ proposal.proposedSolution }}</p>
      </div>

      <div class="export-actions">
        <button (click)="exportPdf()" [disabled]="!proposal?.pdfBase64">
          Export PDF
        </button>
        <button (click)="exportPowerPoint()" [disabled]="!proposal">
          Export PowerPoint
        </button>
        <button (click)="copyExecutiveSummary()" [disabled]="!proposal">
          Copy Executive Summary
        </button>
      </div>
    </div>
  </div>
</div>
