import { Component, OnInit } from "@angular/core";
import html2canvas from "html2canvas";
import { SimulationService } from "../../services/simulation.service";
import { ZoneService } from "../../services/zone.service";
import { Zone } from "../../models/zone.model";
import {
  SimulationRequest,
  SimulationResult,
} from "../../models/simulation.model";

@Component({
  selector: "app-simulation-controls",
  templateUrl: "./simulation-controls.component.html",
  styleUrls: ["./simulation-controls.component.css"],
})
export class SimulationControlsComponent implements OnInit {
  zones: Zone[] = [];
  selectedZoneId: string = "";
  simulationDate: string = new Date().toISOString().split("T")[0];
  simulationDateTime: Date = new Date();

  // Zone rules
  peakMultiplier: number = 1.5;
  diversionThreshold: number = 60;
  exemptionsEnabled: boolean = true;
  freeHours: number = 0;
  customChargePerHour: number | null = null;
  scenarioExemptions: string[] | null = null;

  isRunning = false;
  result: SimulationResult | null = null;
  economicImpact: any | null = null;
  isEconomicLoading = false;
  error: string | null = null;

  presentationMode = false;
  presentationStep = 0;
  presentationScript = [
    {
      title: "UAE Congestion Challenge",
      text: "UAE cities lose billions of dirhams each year to congestion and delays.",
      duration: 5500,
    },
    {
      title: "Smart Zone Pricing",
      text: "Pricing targets the worst bottlenecks with fair exemptions and guardrails.",
      duration: 5500,
    },
    {
      title: "Pilot Scenario",
      text: "Mussafah pilot reduces congestion while generating sustainable revenue.",
      duration: 6000,
    },
    {
      title: "Economic Impact",
      text: "Productivity gains and a positive equity balance support wider adoption.",
      duration: 6000,
    },
    {
      title: "Nationwide Rollout",
      text: "Phased expansion delivers strong ROI and measurable mobility gains.",
      duration: 6000,
    },
  ];
  presentationScenarioQueue = [
    "Demo Scenario",
    "Solve School Run Chaos",
    "Weekend Marina Gridlock",
    "Sharjah-Dubai Commuter Solution",
  ];
  currentScenarioLabel = "";
  autoRunScenarios = true;
  isFullScreen = false;
  private presentationTimer: number | null = null;
  private scenarioTimer: number | null = null;

  scenarios = [
    {
      name: "Demo Scenario",
      zoneName: "Musaffah Industrial",
      freeHours: 2,
      chargePerHour: 30,
      exemptions: ["Commercial", "Emergency"],
      description: "Pilot congestion pricing in Mussafah industrial zone.",
    },
    {
      name: "Solve School Run Chaos",
      zoneName: "Academic City",
      freeHours: 1,
      chargePerHour: 20,
      exemptions: ["Public Transport", "Emergency"],
      description: "Target school commute peaks with mild pricing.",
    },
    {
      name: "Weekend Marina Gridlock",
      zoneName: "Dubai Marina",
      freeHours: 1,
      chargePerHour: 35,
      exemptions: ["Public Transport", "Emergency"],
      description: "Weekend surcharge to reduce marina congestion.",
    },
    {
      name: "Sharjah-Dubai Commuter Solution",
      zoneName: "Al Qusais",
      freeHours: 2,
      chargePerHour: 25,
      exemptions: ["Public Transport", "Commercial", "Emergency"],
      description: "Shift commuter demand with targeted pricing.",
    },
  ];

  constructor(
    private simulationService: SimulationService,
    private zoneService: ZoneService,
  ) {}

  ngOnInit(): void {
    this.loadZones();
  }

  get equityDisplay() {
    return this.normalizeEquity(this.result?.equityImpact);
  }

  get warnings(): string[] {
    if (!this.result) return [];
    const direct = Array.isArray(this.result.warnings)
      ? this.result.warnings
      : [];
    const fromResults = Array.isArray(this.result.results?.warnings)
      ? this.result.results.warnings
      : [];
    return [...new Set([...direct, ...fromResults])];
  }

  get assumptions(): string[] {
    if (!this.result) return [];
    return Array.isArray(this.result.results?.assumptions)
      ? this.result.results.assumptions
      : [
          "Average trip distance assumed at 10 km.",
          "CO₂ savings estimated per minute saved.",
          "Compliance uses a heuristic (not calibrated).",
          "Diversion rules are threshold-based, not a demand model.",
        ];
  }

  get confidenceLabel(): string {
    return this.getConfidenceLabel(this.result?.confidenceScore);
  }

  get dataProvenance(): string {
    if (!this.result) return "Not available";
    const warningText = this.warnings.join(" ").toLowerCase();
    if (warningText.includes("no vehicle movements"))
      return "Insufficient data (no movements for date).";
    if (warningText.includes("fallback"))
      return "Fallback baseline window used.";
    if (this.result.totalVehicles > 0)
      return "Replayed from stored movements (not real-time).";
    return "Not provided by backend.";
  }

  get movementCount(): number {
    return this.result?.totalVehicles ?? 0;
  }

  get baselinePolicy(): {
    baseline: any;
    policy: any;
    delta: any;
  } {
    if (!this.result) {
      return { baseline: null, policy: null, delta: null };
    }

    const baseline = this.result.results?.baseline ?? {
      averageSpeed: this.result.baselineAverageSpeed,
      congestionLevel: this.result.baselineCongestionLevel,
    };

    const policy = this.result.results?.policy ?? {
      averageSpeed: this.result.projectedAverageSpeed,
      congestionLevel: this.result.projectedCongestionLevel,
    };

    const delta = this.result.results?.delta ?? null;

    return { baseline, policy, delta };
  }

  loadZones(): void {
    this.zoneService.getAllZones().subscribe({
      next: (zones: Zone[]) => {
        this.zones = zones;
        if (zones.length > 0) {
          this.selectedZoneId = zones[0].id;
        }
      },
      error: (err: any) => {
        this.error = "Failed to load zones";
      },
    });
  }

  runSimulation(): void {
    if (!this.selectedZoneId) {
      this.error = "Please select a zone";
      return;
    }

    this.isRunning = true;
    this.error = null;

    const exemptions =
      this.scenarioExemptions ??
      (this.exemptionsEnabled
        ? ["Public Transport", "Commercial", "Emergency", "Diplomatic"]
        : []);

    const request: SimulationRequest = {
      zoneId: this.selectedZoneId,
      date: this.simulationDateTime,
      zoneRules: {
        peakMultiplier: this.peakMultiplier,
        diversionThreshold: this.diversionThreshold,
        exemptions,
        freeHours: this.freeHours,
        customChargePerHour: this.customChargePerHour ?? undefined,
      },
    };

    this.simulationService.runSimulation(request).subscribe({
      next: (result: SimulationResult) => {
        this.result = result;
        this.loadEconomicImpact(result.id);
        this.isRunning = false;
      },
      error: (err: any) => {
        this.error = "Failed to run simulation";
        this.isRunning = false;
        console.error(err);
      },
    });
  }

  getConfidenceLabel(score?: number): string {
    if (score === null || score === undefined) return "Not provided";
    if (score < 0.4) return "Low";
    if (score < 0.7) return "Medium";
    return "High";
  }

  formatDelta(value: number | null | undefined, unit: string = ""): string {
    if (value === null || value === undefined || Number.isNaN(value))
      return "—";
    const arrow = value > 0 ? "↑" : value < 0 ? "↓" : "→";
    const abs = Math.abs(value);
    return `${arrow} ${abs.toFixed(2)}${unit}`;
  }

  getWarningCategory(text: string): "Info" | "Caution" {
    const lower = text.toLowerCase();
    if (
      lower.includes("no vehicle movements") ||
      lower.includes("fallback") ||
      lower.includes("out of range") ||
      lower.includes("clamped")
    ) {
      return "Caution";
    }
    return "Info";
  }

  private normalizeEquity(equity: any): {
    lowIncomeBurden: number;
    highIncomeBurden: number;
    burdenRatio: number;
    isRegressive: boolean;
    note?: string;
  } {
    if (equity && typeof equity === "object") {
      return {
        lowIncomeBurden: Number(equity.lowIncomeBurden ?? 0),
        highIncomeBurden: Number(equity.highIncomeBurden ?? 0),
        burdenRatio: Number(equity.burdenRatio ?? 0),
        isRegressive: Boolean(equity.isRegressive ?? false),
      };
    }

    if (typeof equity === "number") {
      return {
        lowIncomeBurden: 0,
        highIncomeBurden: 0,
        burdenRatio: equity,
        isRegressive: false,
        note: "Equity detail not provided; showing aggregate score only.",
      };
    }

    return {
      lowIncomeBurden: 0,
      highIncomeBurden: 0,
      burdenRatio: 0,
      isRegressive: false,
      note: "Equity detail not available.",
    };
  }

  exportResult(format: "pdf" | "csv" | "json"): void {
    if (!this.result) return;

    this.simulationService
      .exportSimulationReport(this.result.id, format)
      .subscribe({
        next: (blob: Blob) => {
          const url = window.URL.createObjectURL(blob);
          const a = document.createElement("a");
          a.href = url;
          a.download = `simulation-${this.result?.id}.${format}`;
          a.click();
          window.URL.revokeObjectURL(url);
        },
        error: (err: any) => {
          this.error = `Failed to export ${format.toUpperCase()}`;
        },
      });
  }

  downloadInvestorReport(): void {
    if (!this.result) return;

    this.simulationService.getInvestorReport(this.result.id).subscribe({
      next: (blob: Blob) => {
        const url = window.URL.createObjectURL(blob);
        const a = document.createElement("a");
        a.href = url;
        a.download = `investor-report-${this.result?.id}.pdf`;
        a.click();
        window.URL.revokeObjectURL(url);
      },
      error: () => {
        this.error = "Failed to download investor report";
      },
    });
  }

  private loadEconomicImpact(simulationId: string): void {
    this.isEconomicLoading = true;
    this.economicImpact = null;

    this.simulationService.getEconomicImpact(simulationId).subscribe({
      next: (impact: any) => {
        this.economicImpact = impact;
        this.isEconomicLoading = false;
      },
      error: () => {
        this.isEconomicLoading = false;
      },
    });
  }

  applyScenario(scenarioName: string, runNow: boolean = true): void {
    const scenario = this.scenarios.find((s) => s.name === scenarioName);
    if (!scenario) return;

    const zone = this.zones.find((z) => z.name === scenario.zoneName);
    if (zone) {
      this.selectedZoneId = zone.id;
    }

    this.freeHours = scenario.freeHours;
    this.customChargePerHour = scenario.chargePerHour;
    this.scenarioExemptions = scenario.exemptions;

    if (runNow) {
      this.runSimulation();
    }
  }

  startPresentation(): void {
    this.presentationMode = true;
    this.presentationStep = 0;
    this.currentScenarioLabel = this.presentationScenarioQueue[0] ?? "";
    this.applyScenario(this.currentScenarioLabel, true);
    this.startAutoPlay();
    this.runScenarioSequence();
  }

  stopPresentation(): void {
    this.presentationMode = false;
    this.presentationStep = 0;
    if (this.presentationTimer) {
      window.clearTimeout(this.presentationTimer);
      this.presentationTimer = null;
    }
    if (this.scenarioTimer) {
      window.clearTimeout(this.scenarioTimer);
      this.scenarioTimer = null;
    }
    if (window.speechSynthesis) {
      window.speechSynthesis.cancel();
    }
    this.exitFullScreen();
  }

  startAutoPlay(): void {
    const playStep = (index: number) => {
      this.presentationStep = index;
      const entry = this.presentationScript[index];
      if (entry?.text) {
        this.speak(entry.text);
      }
      if (index < this.presentationScript.length - 1) {
        const duration = entry?.duration ?? 6000;
        this.presentationTimer = window.setTimeout(
          () => playStep(index + 1),
          duration,
        );
      }
    };

    playStep(0);
  }

  nextPresentationStep(): void {
    const next = Math.min(
      this.presentationStep + 1,
      this.presentationScript.length - 1,
    );
    this.presentationStep = next;
    const entry = this.presentationScript[next];
    if (entry?.text) {
      this.speak(entry.text);
    }
  }

  previousPresentationStep(): void {
    const prev = Math.max(this.presentationStep - 1, 0);
    this.presentationStep = prev;
    const entry = this.presentationScript[prev];
    if (entry?.text) {
      this.speak(entry.text);
    }
  }

  toggleScenarioAutoRun(): void {
    this.autoRunScenarios = !this.autoRunScenarios;
    if (this.autoRunScenarios) {
      this.runScenarioSequence();
    } else if (this.scenarioTimer) {
      window.clearTimeout(this.scenarioTimer);
      this.scenarioTimer = null;
    }
  }

  toggleFullScreen(): void {
    if (document.fullscreenElement) {
      this.exitFullScreen();
    } else {
      this.enterFullScreen();
    }
  }

  private enterFullScreen(): void {
    const root = document.getElementById("presentation-root");
    if (root?.requestFullscreen) {
      root.requestFullscreen();
      this.isFullScreen = true;
    }
  }

  private exitFullScreen(): void {
    if (document.fullscreenElement && document.exitFullscreen) {
      document.exitFullscreen();
    }
    this.isFullScreen = false;
  }

  private runScenarioSequence(): void {
    if (!this.autoRunScenarios || this.presentationScenarioQueue.length === 0) {
      return;
    }

    const runScenarioAt = (index: number) => {
      if (!this.autoRunScenarios) return;

      const scenarioName = this.presentationScenarioQueue[index];
      this.currentScenarioLabel = scenarioName;
      this.applyScenario(scenarioName, true);

      if (index < this.presentationScenarioQueue.length - 1) {
        this.scenarioTimer = window.setTimeout(
          () => runScenarioAt(index + 1),
          12000,
        );
      }
    };

    runScenarioAt(0);
  }

  exportPresentationVideo(): void {
    const target = document.getElementById("presentation-root");
    if (!target) return;

    html2canvas(target).then((canvas) => {
      const stream = canvas.captureStream(30);
      const recorder = new MediaRecorder(stream, { mimeType: "video/webm" });
      const chunks: BlobPart[] = [];

      recorder.ondataavailable = (event) => {
        if (event.data.size > 0) chunks.push(event.data);
      };

      recorder.onstop = () => {
        const blob = new Blob(chunks, { type: "video/webm" });
        const url = URL.createObjectURL(blob);
        const a = document.createElement("a");
        a.href = url;
        a.download = "presentation.webm";
        a.click();
        URL.revokeObjectURL(url);
      };

      recorder.start();
      window.setTimeout(() => recorder.stop(), 5000);
    });
  }

  private speak(text: string): void {
    if (!window.speechSynthesis) return;
    const utterance = new SpeechSynthesisUtterance(text);
    utterance.rate = 1;
    utterance.pitch = 1;
    window.speechSynthesis.cancel();
    window.speechSynthesis.speak(utterance);
  }
}
<div class="controls-container">
  <h2>Simulation Controls</h2>

  <div *ngIf="error" class="error-message">✗ {{ error }}</div>

  <div class="controls-layout">
    <div class="control-panel">
      <div class="form-group">
        <label>Scenarios</label>
        <div class="scenario-buttons">
          <button
            *ngFor="let scenario of scenarios"
            type="button"
            class="btn-scenario"
            (click)="applyScenario(scenario.name)"
          >
            {{ scenario.name }}
          </button>
        </div>
      </div>

      <div class="form-group">
        <label>Select Zone</label>
        <select [(ngModel)]="selectedZoneId" name="zoneId">
          <option value="">-- Select a Zone --</option>
          <option *ngFor="let zone of zones" [value]="zone.id">
            {{ zone.name }} ({{ zone.emirate }})
          </option>
        </select>
      </div>

      <div class="form-group">
        <label>Simulation Date & Time</label>
        <app-date-time-picker
          [label]="'Simulation Date'"
          [timeLabel]="'Time'"
          [showQuickActions]="true"
          [(ngModel)]="simulationDateTime"
          name="simulationDateTime"
        ></app-date-time-picker>
      </div>

      <div class="form-group">
        <label>Peak Hour Multiplier</label>
        <div class="range-control">
          <input
            type="range"
            [(ngModel)]="peakMultiplier"
            name="peakMultiplier"
            min="1"
            max="3"
            step="0.1"
          />
          <span class="value">{{ peakMultiplier.toFixed(1) }}x</span>
        </div>
      </div>

      <div class="form-group">
        <label>Diversion Threshold (minutes)</label>
        <div class="range-control">
          <input
            type="range"
            [(ngModel)]="diversionThreshold"
            name="threshold"
            min="30"
            max="120"
            step="5"
          />
          <span class="value">{{ diversionThreshold }} min</span>
        </div>
      </div>

      <div class="form-group checkbox">
        <label>
          <input
            type="checkbox"
            [(ngModel)]="exemptionsEnabled"
            name="exemptions"
          />
          Enable Exemptions (Public Transport, Commercial, Emergency,
          Diplomatic)
        </label>
      </div>

      <div class="actions">
        <button
          (click)="runSimulation()"
          class="btn-run"
          [disabled]="isRunning || !selectedZoneId"
        >
          {{ isRunning ? "Running Simulation..." : "Run Simulation" }}
        </button>
        <button
          type="button"
          class="btn-run secondary"
          (click)="startPresentation()"
        >
          Presentation Mode
        </button>
      </div>
    </div>

    <div class="results-preview">
      <h3>Quick Results</h3>
      <div *ngIf="result; else emptyResults">
        <div class="metrics-grid">
          <div class="metric">
            <h4>Total Vehicles</h4>
            <p class="value">{{ result.totalVehicles | number }}</p>
          </div>
          <div class="metric">
            <h4>Diverted Vehicles</h4>
            <p class="value">{{ result.vehiclesDiverted | number }}</p>
          </div>
          <div class="metric">
            <h4>Congestion Reduction</h4>
            <p class="value">
              {{ result.congestionReduction | number: "1.1-1" }}%
            </p>
          </div>
          <div class="metric">
            <h4>Estimated Revenue</h4>
            <p class="value">AED {{ result.estimatedRevenue | number }}</p>
          </div>
          <div class="metric">
            <h4>CO₂ Reduction</h4>
            <p class="value">{{ result.environmentalImpact | number }} kg</p>
          </div>
          <div class="metric" [class.regressive]="equityDisplay.isRegressive">
            <h4>Equity Impact</h4>
            <p class="value">
              {{ equityDisplay.isRegressive ? "⚠️ Regressive" : "✓ Fair" }}
            </p>
          </div>
        </div>

        <div class="economic-impact">
          <h4>Economic Impact</h4>
          <div *ngIf="isEconomicLoading" class="loading">Loading impact...</div>
          <div *ngIf="!isEconomicLoading && economicImpact">
            <div class="impact-row">
              <span>NPV (5 yrs):</span>
              <strong
                >AED {{ economicImpact.revenueProjection.npv | number }}</strong
              >
            </div>
            <div class="impact-row">
              <span>Annual Revenue:</span>
              <strong
                >AED
                {{
                  economicImpact.revenueProjection.baseAnnualRevenue | number
                }}</strong
              >
            </div>
            <div class="impact-row">
              <span>Productivity Gain (daily):</span>
              <strong
                >AED
                {{
                  economicImpact.productivity.dailyProductivityGainAed | number
                }}</strong
              >
            </div>
            <div class="impact-row">
              <span>Equity (Low vs High):</span>
              <strong
                >{{ economicImpact.equity.lowIncomeBurden | number: "1.2-2" }}%
                /
                {{
                  economicImpact.equity.highIncomeBurden | number: "1.2-2"
                }}%</strong
              >
            </div>
          </div>
        </div>

        <div class="credibility-panels">
          <details class="credibility-panel">
            <summary>Assumptions & Confidence</summary>
            <div class="panel-body">
              <div class="impact-row">
                <span>Confidence (qualitative):</span>
                <strong>{{ confidenceLabel }}</strong>
              </div>
              <div class="impact-row">
                <span>Data provenance:</span>
                <strong>{{ dataProvenance }}</strong>
              </div>
              <div class="impact-row">
                <span>Assumptions:</span>
              </div>
              <ul>
                <li *ngFor="let assumption of assumptions">{{ assumption }}</li>
              </ul>
              <p class="disclaimer">
                Outputs are directional estimates for sandbox use only.
              </p>
            </div>
          </details>

          <div class="credibility-panel">
            <h4>Baseline vs Policy Comparison</h4>
            <table class="comparison-table">
              <thead>
                <tr>
                  <th></th>
                  <th>Baseline</th>
                  <th>Policy</th>
                  <th>Delta</th>
                </tr>
              </thead>
              <tbody>
                <tr>
                  <td>Revenue (AED)</td>
                  <td>Not available</td>
                  <td>{{ result.estimatedRevenue | number }}</td>
                  <td>—</td>
                </tr>
                <tr>
                  <td>Congestion proxy</td>
                  <td>
                    {{ baselinePolicy.baseline?.congestionLevel ?? "N/A" }}
                  </td>
                  <td>
                    {{ baselinePolicy.policy?.congestionLevel ?? "N/A" }}
                  </td>
                  <td>
                    {{ formatDelta(baselinePolicy.delta?.congestionLevel) }}
                  </td>
                </tr>
                <tr>
                  <td>Equity impact</td>
                  <td>Not available</td>
                  <td>
                    {{
                      equityDisplay.isRegressive
                        ? "Regressive"
                        : "Not regressive"
                    }}
                  </td>
                  <td>—</td>
                </tr>
                <tr>
                  <td>Compliance</td>
                  <td>Not available</td>
                  <td>
                    {{ result.complianceRate ?? "Not available" }}
                  </td>
                  <td>—</td>
                </tr>
              </tbody>
            </table>
            <p class="disclaimer">
              Baseline values shown when provided; otherwise marked as not
              available.
            </p>
          </div>

          <div class="credibility-panel">
            <h4>Data Coverage</h4>
            <div class="impact-row">
              <span>Movements used:</span>
              <strong>{{ movementCount | number }}</strong>
            </div>
            <div class="impact-row">
              <span>Fallback usage:</span>
              <strong>{{
                warnings.join(" ").toLowerCase().includes("fallback")
                  ? "Yes"
                  : "No"
              }}</strong>
            </div>
            <div class="impact-row">
              <span>Missing data warnings:</span>
              <strong>{{ warnings.length > 0 ? "Yes" : "No" }}</strong>
            </div>
          </div>

          <div class="credibility-panel" *ngIf="warnings.length > 0">
            <h4>Warnings & Validation</h4>
            <ul>
              <li *ngFor="let warning of warnings">
                <strong>{{ getWarningCategory(warning) }}:</strong>
                {{ warning }}
              </li>
            </ul>
            <p class="disclaimer">
              Warnings are informational and do not block the simulation.
            </p>
          </div>
        </div>

        <div class="export-actions">
          <button (click)="exportResult('pdf')" class="btn-export">
            Export PDF
          </button>
          <button (click)="downloadInvestorReport()" class="btn-export">
            Download Investor Report
          </button>
          <button (click)="exportResult('csv')" class="btn-export">
            Export CSV
          </button>
          <button (click)="exportResult('json')" class="btn-export">
            Export JSON
          </button>
        </div>
      </div>
      <ng-template #emptyResults>
        <p class="empty-results">Run a simulation to see results here.</p>
      </ng-template>
    </div>
  </div>

  <div
    *ngIf="presentationMode"
    id="presentation-root"
    class="presentation-overlay"
  >
    <div class="presentation-card">
      <h2>Smart Zone Demo</h2>
      <p class="presentation-step">
        Step {{ presentationStep + 1 }} of {{ presentationScript.length }}
      </p>
      <div class="presentation-progress">
        <div
          class="presentation-progress-bar"
          [style.width.%]="
            ((presentationStep + 1) / presentationScript.length) * 100
          "
        ></div>
      </div>
      <p class="presentation-title">
        {{ presentationScript[presentationStep].title }}
      </p>
      <p class="presentation-text">
        {{ presentationScript[presentationStep].text }}
      </p>
      <div class="presentation-status">
        <div>
          <span class="label">Scenario:</span>
          <strong>{{ currentScenarioLabel || "Manual" }}</strong>
        </div>
        <div>
          <span class="label">Auto-run:</span>
          <strong>{{ autoRunScenarios ? "On" : "Off" }}</strong>
        </div>
        <div>
          <span class="label">Full screen:</span>
          <strong>{{ isFullScreen ? "On" : "Off" }}</strong>
        </div>
      </div>

      <div class="presentation-metrics" *ngIf="result">
        <div class="metric">
          <h4>Revenue</h4>
          <p class="value">AED {{ result.estimatedRevenue | number }}</p>
        </div>
        <div class="metric">
          <h4>Congestion</h4>
          <p class="value">
            {{ result.congestionReduction | number: "1.1-1" }}%
          </p>
        </div>
        <div class="metric">
          <h4>Vehicles</h4>
          <p class="value">{{ result.totalVehicles | number }}</p>
        </div>
        <div class="metric">
          <h4>NPV</h4>
          <p class="value">
            AED {{ economicImpact?.revenueProjection?.npv | number }}
          </p>
        </div>
      </div>
      <div class="presentation-actions">
        <button
          class="btn-export secondary"
          (click)="previousPresentationStep()"
        >
          Previous
        </button>
        <button class="btn-export" (click)="nextPresentationStep()">
          Next
        </button>
        <button class="btn-export" (click)="toggleScenarioAutoRun()">
          {{ autoRunScenarios ? "Stop Auto-run" : "Auto-run Scenarios" }}
        </button>
        <button class="btn-export" (click)="toggleFullScreen()">
          {{ isFullScreen ? "Exit Full Screen" : "Full Screen" }}
        </button>
        <button class="btn-export" (click)="exportPresentationVideo()">
          Export Presentation Video
        </button>
        <button class="btn-export secondary" (click)="stopPresentation()">
          Exit Presentation
        </button>
      </div>
    </div>
  </div>
</div>
