import {
  Component,
  ElementRef,
  OnDestroy,
  OnInit,
  ViewChild,
} from "@angular/core";
import { ActivatedRoute, Router } from "@angular/router";
import { jsPDF } from "jspdf";

interface SlideData {
  id: string;
  title: string;
  subtitle?: string;
  content: SlideContent;
  notes: string;
}

interface SlideContent {
  type:
    | "title"
    | "text"
    | "bullet"
    | "split"
    | "chart"
    | "quote"
    | "stats"
    | "image";
  headline?: string;
  subtitle?: string;
  body?: string;
  bullets?: string[];
  leftText?: string;
  rightText?: string;
  stats?: { label: string; value: string; change?: string }[];
  quote?: string;
  quotee?: string;
  imageUrl?: string;
}

interface DeckTemplate {
  id: string;
  name: string;
  description: string;
  slides: SlideData[];
}

@Component({
  selector: "app-pitch-deck",
  template: `
    <div class="pitch-container">
      <!-- Header -->
      <div class="deck-header-bar">
        <div class="header-left">
          <span class="deck-logo">📊</span>
          <div class="header-title">
            <h1>Smart Zone Simulator</h1>
            <span class="header-subtitle">Investor Pitch Deck</span>
          </div>
        </div>
        <div class="header-actions">
          <button class="btn-header" (click)="exportPdf()">
            📄 Export PDF
          </button>
          <button class="btn-header primary" (click)="startPresentation()">
            ▶ Present
          </button>
        </div>
      </div>

      <!-- Main Grid -->
      <div class="deck-main">
        <!-- Sidebar -->
        <div class="deck-sidebar">
          <div class="sidebar-section">
            <h3>📑 Deck Templates</h3>
            <div class="template-list">
              <button
                *ngFor="let t of templates"
                class="template-item"
                [class.active]="selectedTemplateId === t.id"
                (click)="selectTemplate(t.id)"
              >
                <span class="template-name">{{ t.name }}</span>
                <span class="template-desc">{{ t.description }}</span>
                <span class="template-slides"
                  >{{ t.slides.length }} slides</span
                >
              </button>
            </div>
          </div>

          <div class="sidebar-section">
            <h3>🎨 Theme</h3>
            <div class="theme-options">
              <button
                *ngFor="let t of themes"
                class="theme-btn"
                [class.active]="selectedTheme.id === t.id"
                (click)="selectTheme(t.id)"
                [style.--theme-primary]="t.colors.primary"
                [style.--theme-secondary]="t.colors.secondary"
              >
                <span class="theme-preview"></span>
                <span>{{ t.name }}</span>
              </button>
            </div>
          </div>

          <div class="sidebar-section">
            <h3>📋 Slides</h3>
            <div class="slides-nav">
              <div
                *ngFor="let slide of currentSlides; let i = index"
                class="slide-nav-item"
                [class.active]="currentSlideIndex === i"
                (click)="goToSlide(i)"
              >
                <span class="slide-number">{{ i + 1 }}</span>
                <span class="slide-title">{{ slide.title }}</span>
              </div>
            </div>
          </div>
        </div>

        <!-- Preview Area -->
        <div class="deck-preview-area">
          <!-- Slide Preview -->
          <div class="slide-preview-container">
            <div
              class="slide-preview"
              [style.--primary]="selectedTheme.colors.primary"
              [style.--secondary]="selectedTheme.colors.secondary"
              [style.--accent]="selectedTheme.colors.accent"
              [style.--bg]="selectedTheme.colors.background"
            >
              <!-- Title Slide -->
              <div
                *ngIf="currentSlide.content.type === 'title'"
                class="slide-title-slide"
              >
                <div class="title-bg-pattern"></div>
                <div class="title-content">
                  <div class="title-logo">🏙️</div>
                  <h1>{{ currentSlide.title }}</h1>
                  <p class="title-subtitle">
                    {{ currentSlide.content.subtitle }}
                  </p>
                  <div class="title-meta">
                    <span>📍 {{ selectedTheme.location }}</span>
                    <span>📅 {{ selectedTheme.date }}</span>
                  </div>
                </div>
              </div>

              <!-- Text Slide -->
              <div
                *ngIf="currentSlide.content.type === 'text'"
                class="slide-text-slide"
              >
                <div class="slide-header">
                  <h2>{{ currentSlide.title }}</h2>
                </div>
                <div class="slide-body">
                  <p class="headline">{{ currentSlide.content.headline }}</p>
                  <p class="body-text">{{ currentSlide.content.body }}</p>
                </div>
              </div>

              <!-- Bullet Slide -->
              <div
                *ngIf="currentSlide.content.type === 'bullet'"
                class="slide-bullet-slide"
              >
                <div class="slide-header">
                  <h2>{{ currentSlide.title }}</h2>
                </div>
                <div class="slide-body">
                  <ul class="bullet-list">
                    <li *ngFor="let bullet of currentSlide.content.bullets">
                      <span class="bullet-icon">▸</span>
                      {{ bullet }}
                    </li>
                  </ul>
                </div>
              </div>

              <!-- Split Slide -->
              <div
                *ngIf="currentSlide.content.type === 'split'"
                class="slide-split-slide"
              >
                <div class="slide-header">
                  <h2>{{ currentSlide.title }}</h2>
                </div>
                <div class="split-content">
                  <div class="split-left">
                    <h3>{{ currentSlide.content.headline }}</h3>
                    <p>{{ currentSlide.content.leftText }}</p>
                  </div>
                  <div class="split-right">
                    <h3>Key Points</h3>
                    <ul>
                      <li *ngFor="let b of currentSlide.content.bullets">
                        {{ b }}
                      </li>
                    </ul>
                  </div>
                </div>
              </div>

              <!-- Stats Slide -->
              <div
                *ngIf="currentSlide.content.type === 'stats'"
                class="slide-stats-slide"
              >
                <div class="slide-header">
                  <h2>{{ currentSlide.title }}</h2>
                </div>
                <div class="stats-grid">
                  <div
                    class="stat-card"
                    *ngFor="let stat of currentSlide.content.stats"
                  >
                    <span class="stat-value">{{ stat.value }}</span>
                    <span class="stat-label">{{ stat.label }}</span>
                    <span class="stat-change" *ngIf="stat.change">{{
                      stat.change
                    }}</span>
                  </div>
                </div>
              </div>

              <!-- Quote Slide -->
              <div
                *ngIf="currentSlide.content.type === 'quote'"
                class="slide-quote-slide"
              >
                <div class="quote-mark">"</div>
                <blockquote>{{ currentSlide.content.quote }}</blockquote>
                <cite>— {{ currentSlide.content.quotee }}</cite>
              </div>
            </div>

            <!-- Navigation -->
            <div class="slide-nav">
              <button
                (click)="prevSlide()"
                [disabled]="currentSlideIndex === 0"
              >
                ← Previous
              </button>
              <span class="slide-counter"
                >{{ currentSlideIndex + 1 }} / {{ currentSlides.length }}</span
              >
              <button
                (click)="nextSlide()"
                [disabled]="currentSlideIndex === currentSlides.length - 1"
              >
                Next →
              </button>
            </div>
          </div>

          <!-- Speaker Notes -->
          <div class="speaker-notes-panel">
            <h4>📝 Speaker Notes</h4>
            <p>
              {{
                currentSlide.notes ||
                  "Click to add speaker notes for this slide."
              }}
            </p>
          </div>
        </div>
      </div>

      <!-- Presentation Mode -->
      <div class="presentation-overlay" *ngIf="isPresenting">
        <div
          class="presentation-slide"
          [style.--primary]="selectedTheme.colors.primary"
          [style.--secondary]="selectedTheme.colors.secondary"
          [style.--accent]="selectedTheme.colors.accent"
          [style.--bg]="selectedTheme.colors.background"
        >
          <!-- Title Slide -->
          <div
            *ngIf="
              presentationSlides[presentationIndex].content.type === 'title'
            "
            class="slide-title-slide pres"
          >
            <div class="title-bg-pattern"></div>
            <div class="title-content">
              <div class="title-logo">🏙️</div>
              <h1>{{ presentationSlides[presentationIndex].title }}</h1>
              <p class="title-subtitle">
                {{ presentationSlides[presentationIndex].content.subtitle }}
              </p>
              <div class="title-meta">
                <span>📍 {{ selectedTheme.location }}</span>
                <span>📅 {{ selectedTheme.date }}</span>
              </div>
            </div>
          </div>

          <!-- Text Slide -->
          <div
            *ngIf="
              presentationSlides[presentationIndex].content.type === 'text'
            "
            class="slide-text-slide pres"
          >
            <div class="slide-header">
              <h2>{{ presentationSlides[presentationIndex].title }}</h2>
            </div>
            <div class="slide-body">
              <p class="headline">
                {{ presentationSlides[presentationIndex].content.headline }}
              </p>
              <p class="body-text">
                {{ presentationSlides[presentationIndex].content.body }}
              </p>
            </div>
          </div>

          <!-- Bullet Slide -->
          <div
            *ngIf="
              presentationSlides[presentationIndex].content.type === 'bullet'
            "
            class="slide-bullet-slide pres"
          >
            <div class="slide-header">
              <h2>{{ presentationSlides[presentationIndex].title }}</h2>
            </div>
            <div class="slide-body">
              <ul class="bullet-list">
                <li
                  *ngFor="
                    let bullet of presentationSlides[presentationIndex].content
                      .bullets
                  "
                >
                  <span class="bullet-icon">▸</span>
                  {{ bullet }}
                </li>
              </ul>
            </div>
          </div>

          <!-- Stats Slide -->
          <div
            *ngIf="
              presentationSlides[presentationIndex].content.type === 'stats'
            "
            class="slide-stats-slide pres"
          >
            <div class="slide-header">
              <h2>{{ presentationSlides[presentationIndex].title }}</h2>
            </div>
            <div class="stats-grid">
              <div
                class="stat-card"
                *ngFor="
                  let stat of presentationSlides[presentationIndex].content
                    .stats
                "
              >
                <span class="stat-value">{{ stat.value }}</span>
                <span class="stat-label">{{ stat.label }}</span>
                <span class="stat-change" *ngIf="stat.change">{{
                  stat.change
                }}</span>
              </div>
            </div>
          </div>

          <!-- Quote Slide -->
          <div
            *ngIf="
              presentationSlides[presentationIndex].content.type === 'quote'
            "
            class="slide-quote-slide pres"
          >
            <div class="quote-mark">"</div>
            <blockquote>
              {{ presentationSlides[presentationIndex].content.quote }}
            </blockquote>
            <cite
              >—
              {{ presentationSlides[presentationIndex].content.quotee }}</cite
            >
          </div>

          <!-- Presentation Controls -->
          <div class="pres-controls">
            <button class="pres-btn" (click)="prevSlide()">←</button>
            <span class="pres-counter"
              >{{ presentationIndex + 1 }} /
              {{ presentationSlides.length }}</span
            >
            <button class="pres-btn" (click)="nextSlide()">→</button>
            <button class="pres-btn exit" (click)="stopPresentation()">
              ✕ Exit
            </button>
          </div>
        </div>
      </div>
    </div>
  `,
  styles: [
    `
      .pitch-container {
        height: 100vh;
        display: flex;
        flex-direction: column;
        background: #f1f5f9;
      }

      /* Header Bar */
      .deck-header-bar {
        display: flex;
        justify-content: space-between;
        align-items: center;
        padding: 12px 24px;
        background: white;
        border-bottom: 1px solid #e2e8f0;
      }

      .header-left {
        display: flex;
        align-items: center;
        gap: 12px;
      }

      .deck-logo {
        font-size: 1.5rem;
      }

      .header-title h1 {
        margin: 0;
        font-size: 1.1rem;
        color: #1e3a5f;
      }

      .header-subtitle {
        font-size: 0.75rem;
        color: #64748b;
      }

      .header-actions {
        display: flex;
        gap: 10px;
      }

      .btn-header {
        padding: 8px 16px;
        border-radius: 8px;
        border: 1px solid #e2e8f0;
        background: white;
        cursor: pointer;
        font-weight: 500;
      }

      .btn-header.primary {
        background: #1e3a5f;
        color: white;
        border: none;
      }

      /* Main Grid */
      .deck-main {
        display: grid;
        grid-template-columns: 280px 1fr;
        flex: 1;
        overflow: hidden;
      }

      /* Sidebar */
      .deck-sidebar {
        background: white;
        border-right: 1px solid #e2e8f0;
        padding: 20px;
        overflow-y: auto;
      }

      .sidebar-section {
        margin-bottom: 24px;
      }

      .sidebar-section h3 {
        margin: 0 0 12px;
        font-size: 0.85rem;
        color: #64748b;
        text-transform: uppercase;
        letter-spacing: 0.5px;
      }

      .template-list {
        display: flex;
        flex-direction: column;
        gap: 8px;
      }

      .template-item {
        padding: 12px;
        border: 2px solid #e2e8f0;
        border-radius: 10px;
        background: white;
        cursor: pointer;
        text-align: left;
        transition: all 0.2s;
      }

      .template-item:hover {
        border-color: #3b82f6;
      }

      .template-item.active {
        border-color: #1e3a5f;
        background: #f8fafc;
      }

      .template-name {
        display: block;
        font-weight: 600;
        color: #1e3a5f;
      }

      .template-desc {
        display: block;
        font-size: 0.75rem;
        color: #64748b;
        margin-top: 2px;
      }

      .template-slides {
        display: block;
        font-size: 0.7rem;
        color: #94a3b8;
        margin-top: 4px;
      }

      /* Theme Options */
      .theme-options {
        display: flex;
        gap: 8px;
        flex-wrap: wrap;
      }

      .theme-btn {
        width: 60px;
        padding: 8px;
        border: 2px solid #e2e8f0;
        border-radius: 8px;
        background: white;
        cursor: pointer;
        font-size: 0.7rem;
      }

      .theme-btn.active {
        border-color: #1e3a5f;
      }

      .theme-preview {
        display: block;
        width: 100%;
        height: 24px;
        border-radius: 4px;
        background: linear-gradient(
          135deg,
          var(--theme-primary) 50%,
          var(--theme-secondary) 50%
        );
      }

      /* Slides Nav */
      .slides-nav {
        display: flex;
        flex-direction: column;
        gap: 4px;
        max-height: 300px;
        overflow-y: auto;
      }

      .slide-nav-item {
        display: flex;
        align-items: center;
        gap: 10px;
        padding: 8px 10px;
        border-radius: 6px;
        cursor: pointer;
        transition: background 0.2s;
      }

      .slide-nav-item:hover {
        background: #f1f5f9;
      }

      .slide-nav-item.active {
        background: #e0f2fe;
      }

      .slide-number {
        width: 24px;
        height: 24px;
        background: #e2e8f0;
        border-radius: 50%;
        display: flex;
        align-items: center;
        justify-content: center;
        font-size: 0.75rem;
        font-weight: 600;
      }

      .slide-nav-item.active .slide-number {
        background: #1e3a5f;
        color: white;
      }

      .slide-title {
        font-size: 0.85rem;
        color: #475569;
        white-space: nowrap;
        overflow: hidden;
        text-overflow: ellipsis;
      }

      /* Preview Area */
      .deck-preview-area {
        padding: 24px;
        display: flex;
        flex-direction: column;
        gap: 16px;
        overflow-y: auto;
      }

      .slide-preview-container {
        flex: 1;
        display: flex;
        flex-direction: column;
        max-width: 900px;
        margin: 0 auto;
        width: 100%;
      }

      .slide-preview {
        aspect-ratio: 16/9;
        background: var(--bg);
        border-radius: 12px;
        overflow: hidden;
        box-shadow: 0 8px 30px rgba(0, 0, 0, 0.12);
      }

      /* Title Slide */
      .slide-title-slide {
        height: 100%;
        display: flex;
        align-items: center;
        justify-content: center;
        position: relative;
        overflow: hidden;
      }

      .title-bg-pattern {
        position: absolute;
        inset: 0;
        background:
          radial-gradient(
            circle at 20% 80%,
            var(--primary) 0%,
            transparent 50%
          ),
          radial-gradient(circle at 80% 20%, var(--accent) 0%, transparent 50%),
          linear-gradient(135deg, var(--bg) 0%, var(--secondary) 100%);
        opacity: 0.15;
      }

      .title-content {
        text-align: center;
        z-index: 1;
        padding: 40px;
      }

      .title-logo {
        font-size: 4rem;
        margin-bottom: 20px;
      }

      .slide-title-slide h1 {
        font-size: 2.5rem;
        color: var(--primary);
        margin: 0 0 10px;
      }

      .title-subtitle {
        font-size: 1.2rem;
        color: #475569;
        margin: 0 0 20px;
      }

      .title-meta {
        display: flex;
        gap: 20px;
        justify-content: center;
        font-size: 0.9rem;
        color: #64748b;
      }

      /* Text Slide */
      .slide-text-slide,
      .slide-bullet-slide,
      .slide-split-slide,
      .slide-stats-slide {
        height: 100%;
        display: flex;
        flex-direction: column;
      }

      .slide-header {
        padding: 30px 40px 20px;
        border-bottom: 1px solid #e2e8f0;
      }

      .slide-header h2 {
        margin: 0;
        font-size: 1.6rem;
        color: var(--primary);
      }

      .slide-body {
        padding: 30px 40px;
        flex: 1;
      }

      .headline {
        font-size: 1.3rem;
        font-weight: 600;
        color: #1e293b;
        margin: 0 0 16px;
      }

      .body-text {
        font-size: 1rem;
        color: #475569;
        line-height: 1.7;
      }

      /* Bullet List */
      .bullet-list {
        list-style: none;
        padding: 0;
        margin: 0;
      }

      .bullet-list li {
        display: flex;
        align-items: flex-start;
        gap: 12px;
        margin-bottom: 16px;
        font-size: 1.05rem;
        color: #334155;
      }

      .bullet-icon {
        color: var(--accent);
        font-weight: bold;
      }

      /* Split Content */
      .split-content {
        display: grid;
        grid-template-columns: 1fr 1fr;
        gap: 40px;
        padding: 30px 40px;
      }

      .split-left h3,
      .split-right h3 {
        color: var(--primary);
        margin: 0 0 12px;
      }

      .split-left p,
      .split-right ul {
        color: #475569;
        line-height: 1.6;
      }

      .split-right ul {
        padding-left: 20px;
      }

      .split-right li {
        margin-bottom: 8px;
      }

      /* Stats Grid */
      .stats-grid {
        display: grid;
        grid-template-columns: repeat(3, 1fr);
        gap: 20px;
        padding: 30px 40px;
      }

      .stat-card {
        background: white;
        border-radius: 12px;
        padding: 24px;
        text-align: center;
        box-shadow: 0 2px 8px rgba(0, 0, 0, 0.06);
      }

      .stat-value {
        display: block;
        font-size: 2rem;
        font-weight: 700;
        color: var(--primary);
      }

      .stat-label {
        display: block;
        font-size: 0.85rem;
        color: #64748b;
        margin-top: 4px;
      }

      .stat-change {
        display: inline-block;
        margin-top: 8px;
        padding: 4px 10px;
        background: #d1fae5;
        color: #059669;
        border-radius: 20px;
        font-size: 0.75rem;
        font-weight: 600;
      }

      /* Quote Slide */
      .slide-quote-slide {
        height: 100%;
        display: flex;
        flex-direction: column;
        align-items: center;
        justify-content: center;
        padding: 60px;
        background: linear-gradient(135deg, var(--primary) 0%, #1e3a5f 100%);
        color: white;
      }

      .quote-mark {
        font-size: 6rem;
        opacity: 0.3;
        line-height: 1;
      }

      .slide-quote-slide blockquote {
        font-size: 1.8rem;
        text-align: center;
        margin: 0;
        font-style: italic;
        line-height: 1.5;
      }

      .slide-quote-slide cite {
        margin-top: 24px;
        font-size: 1rem;
        opacity: 0.8;
        font-style: normal;
      }

      /* Slide Nav */
      .slide-nav {
        display: flex;
        justify-content: center;
        align-items: center;
        gap: 20px;
        padding: 16px;
      }

      .slide-nav button {
        padding: 10px 20px;
        border: 1px solid #e2e8f0;
        border-radius: 8px;
        background: white;
        cursor: pointer;
        font-weight: 500;
      }

      .slide-nav button:disabled {
        opacity: 0.5;
        cursor: not-allowed;
      }

      .slide-counter {
        font-size: 0.9rem;
        color: #64748b;
      }

      /* Speaker Notes */
      .speaker-notes-panel {
        background: white;
        border-radius: 10px;
        padding: 16px;
        border: 1px solid #e2e8f0;
      }

      .speaker-notes-panel h4 {
        margin: 0 0 8px;
        font-size: 0.85rem;
        color: #64748b;
      }

      .speaker-notes-panel p {
        margin: 0;
        font-size: 0.9rem;
        color: #475569;
      }

      /* Presentation Overlay */
      .presentation-overlay {
        position: fixed;
        inset: 0;
        background: #0f172a;
        z-index: 9999;
        display: flex;
        align-items: center;
        justify-content: center;
      }

      .presentation-slide {
        width: 90vw;
        max-width: 1200px;
        aspect-ratio: 16/9;
        background: var(--bg);
        border-radius: 16px;
        overflow: hidden;
        position: relative;
      }

      .slide-title-slide.pres,
      .slide-text-slide.pres,
      .slide-bullet-slide.pres,
      .slide-stats-slide.pres {
        font-size: 1.2em;
      }

      .pres-controls {
        position: absolute;
        bottom: 20px;
        left: 50%;
        transform: translateX(-50%);
        display: flex;
        gap: 12px;
        background: rgba(0, 0, 0, 0.7);
        padding: 10px 20px;
        border-radius: 30px;
      }

      .pres-btn {
        background: transparent;
        border: none;
        color: white;
        cursor: pointer;
        padding: 8px 16px;
        border-radius: 20px;
        font-size: 1rem;
      }

      .pres-btn:hover {
        background: rgba(255, 255, 255, 0.2);
      }

      .pres-btn.exit {
        background: #dc2626;
      }

      .pres-counter {
        color: white;
        display: flex;
        align-items: center;
        padding: 0 12px;
      }
    `,
  ],
})
export class PitchDeckComponent implements OnInit, OnDestroy {
