export class ZoneMapComponent {
  // Get zones grouped by emirate
  get zonesByEmirate() {
    const grouped = this.zones.reduce((acc, zone) => {
      const emirate = zone.emirate || "Other";
      if (!acc[emirate]) {
        acc[emirate] = [];
      }
      acc[emirate].push(zone);
      return acc;
    }, {});
    return Object.entries(grouped).map(([emirate, zones]) => ({
      emirate,
      zones
    })).sort((a, b) => a.emirate.localeCompare(b.emirate));
  }
  constructor(zoneService, trafficLayerService) {
    this.zoneService = zoneService;
    this.trafficLayerService = trafficLayerService;
    this.zones = [];
    this.selectedZone = null;
    this.mapCenter = {
      lat: 24.4539,
      lng: 54.3773
    }; // UAE center
    this.mapZoom = 8;
    this.timeIndex = new Date().getHours();
    this.isAnimating = false;
    this.animationSpeed = 1500;
    this.isLoading = false;
    this.error = null;
    this.trafficMode = "historical";
    this.liveTrafficAvailable = false;
    this.lastTrafficUpdate = null;
    this.map = null;
    this.markerLayer = null;
    this.polygonLayer = null;
    this.refreshHandle = null;
    this.heatmapTimer = null;
    this.zoneLayers = new Map();
    this.zoneBounds = new Map();
  }
  ngOnInit() {
    this.loadZones();
    this.refreshHandle = window.setInterval(() => this.loadZones(false), 15000);
  }
  ngAfterViewInit() {
    this.initializeMap();
  }
  ngOnDestroy() {
    if (this.refreshHandle) {
      window.clearInterval(this.refreshHandle);
    }
    if (this.heatmapTimer) {
      window.clearInterval(this.heatmapTimer);
    }
    this.trafficLayerService.stopLiveUpdates();
    if (this.map) {
      this.map.remove();
    }
  }
  loadZones(showLoading = true) {
    this.isLoading = showLoading;
    this.zoneService.getAllZones().subscribe({
      next: zones => {
        this.zones = zones;
        this.isLoading = false;
        this.renderZonePolygons();
      },
      error: err => {
        this.error = "Failed to load zones";
        this.isLoading = false;
        console.error(err);
      }
    });
  }
  onZoneSelected(zone) {
    this.selectedZone = zone;
    // Center map on selected zone
    const coords = this.getZoneCoordinates(zone);
    this.mapCenter = {
      lat: coords[0],
      lng: coords[1]
    };
    this.mapZoom = 11;
    if (this.map) {
      this.map.setView(coords, this.mapZoom);
    }
    this.refreshTrafficLayer();
  }
  filterByEmirate(emirate) {
    if (emirate === "All") {
      this.loadZones();
      this.setMapViewByEmirate(emirate);
    } else {
      this.isLoading = true;
      this.zoneService.getZonesByEmirate(emirate).subscribe({
        next: zones => {
          this.zones = zones;
          this.isLoading = false;
          this.renderZonePolygons();
          this.setMapViewByEmirate(emirate);
        },
        error: err => {
          this.error = `Failed to load zones for ${emirate}`;
          this.isLoading = false;
        }
      });
    }
  }
  onTimeChange() {
    this.updateHeatmapStyles();
    if (this.trafficMode === "historical" && this.selectedZone) {
      this.showHistoricalTraffic();
    }
  }
  toggleAnimation() {
    if (this.isAnimating) {
      this.stopAnimation();
    } else {
      this.startAnimation();
    }
  }
  startAnimation() {
    if (this.heatmapTimer) {
      window.clearInterval(this.heatmapTimer);
    }
    this.isAnimating = true;
    this.heatmapTimer = window.setInterval(() => {
      this.timeIndex = (this.timeIndex + 1) % 24;
      this.updateHeatmapStyles();
    }, this.animationSpeed);
  }
  stopAnimation() {
    if (this.heatmapTimer) {
      window.clearInterval(this.heatmapTimer);
      this.heatmapTimer = null;
    }
    this.isAnimating = false;
  }
  onSpeedChange() {
    if (this.isAnimating) {
      this.startAnimation();
    }
  }
  formatHour(hour) {
    const normalized = (hour % 24 + 24) % 24;
    const suffix = normalized >= 12 ? "PM" : "AM";
    const display = normalized % 12 === 0 ? 12 : normalized % 12;
    return `${display}:00 ${suffix}`;
  }
  getZoneColor(zoneType) {
    switch (zoneType) {
      case "Residential":
        return "#4CAF50";
      case "Commercial":
        return "#FF9800";
      case "Industrial":
        return "#9C27B0";
      case "Mixed":
        return "#2196F3";
      default:
        return "#757575";
    }
  }
  initializeMap() {
    const defaultIcon = L.icon({
      iconRetinaUrl: "https://unpkg.com/leaflet@1.9.4/dist/images/marker-icon-2x.png",
      iconUrl: "https://unpkg.com/leaflet@1.9.4/dist/images/marker-icon.png",
      shadowUrl: "https://unpkg.com/leaflet@1.9.4/dist/images/marker-shadow.png",
      iconSize: [25, 41],
      iconAnchor: [12, 41],
      popupAnchor: [1, -34],
      shadowSize: [41, 41]
    });
    L.Marker.prototype.options.icon = defaultIcon;
    this.map = L.map("zone-map", {
      center: [this.mapCenter.lat, this.mapCenter.lng],
      zoom: this.mapZoom
