        fillColor: color,
        fillOpacity: 0.4
      });
      this.updateLayerPopup(layer, zone, congestion);
    });
  }
  updateLayerPopup(layer, zone, congestion) {
    const stats = this.getZoneStats(zone, congestion);
    layer.bindPopup(`<strong>${zone.name}</strong><br/>${zone.emirate} • ${zone.zoneType}` + `<br/>Time: ${this.formatHour(this.timeIndex)}` + `<br/>Vehicles/hr: ${stats.vehiclesPerHour}` + `<br/>Avg speed: ${stats.avgSpeed} km/h` + `<br/>Congestion: ${stats.congestion}%`);
  }
  getZoneJitter(seed) {
