  selectScenario(type) {
    switch (type) {
      case "mussafah":
        this.selectedZone = this.zones.find(z => z.name.includes("Mussafah") || z.name.includes("Musaffah")) || null;
        break;
      case "marina":
        this.selectedZone = this.zones.find(z => z.name.includes("Marina")) || null;
        break;
      case "academic":
        this.selectedZone = this.zones.find(z => z.name.includes("Academic") || z.name.includes("City")) || null;
        break;
      case "qusais":
        this.selectedZone = this.zones.find(z => z.name.includes("Qusais")) || null;
        break;
    }
    if (this.selectedZone) {
      this.config.hourlyRate = this.selectedZone.chargePerHour;
      this.config.freeHours = this.selectedZone.baseFreeHours;
    }
  }
  applyPreset(type) {
    switch (type) {
      case "light":
        this.config.hourlyRate = 10;
        this.config.freeHours = 2;
        this.config.peakMultiplier = 1.2;
        break;
      case "moderate":
        this.config.hourlyRate = 25;
        this.config.freeHours = 1;
        this.config.peakMultiplier = 1.5;
        break;
      case "aggressive":
        this.config.hourlyRate = 50;
        this.config.freeHours = 0;
        this.config.peakMultiplier = 2;
        break;
    }
