sourcesContent:
import { Component, OnInit } from "@angular/core";
import { PilotProposalService } from "../../services/pilot-proposal.service";
import { SimulationService } from "../../services/simulation.service";
import { SimulationResult } from "../../models/simulation.model";
import {
  PilotOptions,
  PilotProposalDto,
} from "../../models/pilot-proposal.model";

interface PilotPhase {
  name: string;
  duration: string;
  activities: string[];
  deliverables: string[];
}

interface CostBreakdown {
  category: string;
  amount: number;
  percentage: number;
}

@Component({
  selector: "app-pilot-proposal",
  template: `
    <div class="pilot-container">
      <!-- Header -->
      <div class="page-header">
        <div class="header-content">
          <div class="header-icon">🚀</div>
          <div>
            <h1>Smart Zone Pilot Proposal</h1>
            <p class="subtitle">
              Government-ready implementation proposal with ROI analysis
            </p>
          </div>
        </div>
      </div>

      <!-- Main Content Grid -->
      <div class="content-grid">
        <!-- Configuration Panel -->
        <div class="config-panel">
          <div class="panel-card">
            <h3>📋 Pilot Configuration</h3>

            <div class="form-group">
              <label>Select Simulation Baseline</label>
              <select [(ngModel)]="selectedSimulationId" class="form-select">
                <option value="">Choose simulation...</option>
                <option *ngFor="let sim of simulations" [value]="sim.id">
                  {{ sim.name }} • AED
                  {{ sim.estimatedRevenue | number: "1.0-0" }}/day
                </option>
              </select>
            </div>

            <div class="template-buttons">
              <button
                class="template-btn"
                [class.active]="selectedTemplate === 'basic'"
                (click)="selectTemplate('basic')"
              >
                <span class="template-icon">📝</span>
                <span class="template-name">Basic Pilot</span>
                <span class="template-cost">AED 250K</span>
              </button>
              <button
                class="template-btn"
                [class.active]="selectedTemplate === 'standard'"
                (click)="selectTemplate('standard')"
              >
                <span class="template-icon">💼</span>
                <span class="template-name">Standard</span>
                <span class="template-cost">AED 535K</span>
              </button>
              <button
                class="template-btn"
                [class.active]="selectedTemplate === 'enterprise'"
                (click)="selectTemplate('enterprise')"
              >
                <span class="template-icon">🏢</span>
                <span class="template-name">Enterprise</span>
                <span class="template-cost">AED 750K</span>
              </button>
            </div>

            <div class="options-section">
              <h4>Configuration Options</h4>

              <label class="option-checkbox">
                <input
                  type="checkbox"
                  [(ngModel)]="options.includeUaePassIntegration"
                />
                <span class="checkbox-custom"></span>
                <span class="option-content">
                  <strong>UAE Pass Integration</strong>
                  <small>Digital identity & payments</small>
                </span>
                <span class="option-cost">+AED 50,000</span>
              </label>

              <label class="option-checkbox">
                <input type="checkbox" [(ngModel)]="options.includeMobileApp" />
                <span class="checkbox-custom"></span>
                <span class="option-content">
                  <strong>Mobile Application</strong>
                  <small>iOS & Android native apps</small>
                </span>
                <span class="option-cost">+AED 75,000</span>
              </label>

              <label class="option-checkbox">
                <input
                  type="checkbox"
                  [(ngModel)]="options.includeHardwareIntegration"
                />
                <span class="checkbox-custom"></span>
                <span class="option-content">
                  <strong>Salik/Hardware Integration</strong>
                  <small>Existing toll gate connectivity</small>
                </span>
                <span class="option-cost">+AED 100,000</span>
              </label>

              <label class="option-checkbox">
                <input
                  type="checkbox"
                  [(ngModel)]="options.include24x7Support"
                />
                <span class="checkbox-custom"></span>
                <span class="option-content">
                  <strong>24/7 Operations Support</strong>
                  <small>Round-the-clock monitoring</small>
                </span>
                <span class="option-cost">+AED 60,000</span>
              </label>
            </div>

            <div class="form-group">
              <label>Pilot Duration: {{ options.durationMonths }} months</label>
              <input
                type="range"
                min="3"
                max="12"
                step="1"
                [(ngModel)]="options.durationMonths"
                class="form-range"
              />
              <div class="range-labels">
                <span>3 mo</span>
                <span>6 mo</span>
                <span>9 mo</span>
                <span>12 mo</span>
              </div>
            </div>

            <button
              class="btn-generate"
              (click)="generateProposal()"
              [disabled]="isGenerating || !selectedSimulationId"
            >
              {{
                isGenerating
                  ? "⏳ Generating Proposal..."
                  : "🚀 Generate Pilot Proposal"
              }}
            </button>
          </div>
        </div>

        <!-- Results Panel -->
        <div class="results-panel">
          <!-- KPI Cards -->
          <div class="kpi-grid">
            <div class="kpi-card primary">
              <div class="kpi-icon">💰</div>
              <div class="kpi-content">
                <span class="kpi-value"
                  >AED {{ getTotalCost() | number: "1.0-0" }}</span
                >
                <span class="kpi-label">Total Pilot Investment</span>
              </div>
            </div>
            <div class="kpi-card success">
              <div class="kpi-icon">📈</div>
              <div class="kpi-content">
                <span class="kpi-value"
                  >AED {{ getEstimatedAnnualRevenue() | number: "1.0-0" }}</span
                >
                <span class="kpi-label">Projected Annual Revenue</span>
              </div>
            </div>
            <div class="kpi-card warning">
              <div class="kpi-icon">⏱️</div>
              <div class="kpi-content">
                <span class="kpi-value"
                  >{{ options.durationMonths }} months</span
                >
                <span class="kpi-label">Implementation Timeline</span>
              </div>
            </div>
            <div class="kpi-card info">
              <div class="kpi-icon">🎯</div>
              <div class="kpi-content">
                <span class="kpi-value">{{ getROI() | number: "1.0-0" }}%</span>
                <span class="kpi-label">Projected ROI</span>
              </div>
            </div>
          </div>

          <!-- Charts Section -->
          <div class="charts-grid">
            <!-- Cost Breakdown Chart -->
            <div class="chart-card">
              <h4>📊 Investment Breakdown</h4>
              <div class="donut-chart">
                <svg viewBox="0 0 100 100" class="donut">
                  <circle
                    cx="50"
                    cy="50"
                    r="40"
                    fill="none"
                    stroke="#e2e8f0"
                    stroke-width="12"
                  />
                  <circle
                    cx="50"
                    cy="50"
                    r="40"
                    fill="none"
                    stroke="#3b82f6"
                    stroke-width="12"
                    [attr.stroke-dasharray]="getDonutStroke(25)"
                    stroke-dashoffset="0"
                    transform="rotate(-90 50 50)"
                  />
                  <circle
                    cx="50"
                    cy="50"
                    r="40"
                    fill="none"
                    stroke="#8b5cf6"
                    stroke-width="12"
                    [attr.stroke-dasharray]="getDonutStroke(15)"
                    [attr.stroke-dashoffset]="getDonutOffset(25)"
                    transform="rotate(-90 50 50)"
                  />
                  <circle
                    cx="50"
                    cy="50"
                    r="40"
                    fill="none"
                    stroke="#06b6d4"
                    stroke-width="12"
                    [attr.stroke-dasharray]="getDonutStroke(20)"
                    [attr.stroke-dashoffset]="getDonutOffset(40)"
                    transform="rotate(-90 50 50)"
                  />
                  <circle
                    cx="50"
                    cy="50"
                    r="40"
                    fill="none"
                    stroke="#10b981"
                    stroke-width="12"
                    [attr.stroke-dasharray]="getDonutStroke(10)"
                    [attr.stroke-dashoffset]="getDonutOffset(60)"
                    transform="rotate(-90 50 50)"
                  />
                  <circle
                    cx="50"
                    cy="50"
                    r="40"
                    fill="none"
                    stroke="#f59e0b"
                    stroke-width="12"
                    [attr.stroke-dasharray]="getDonutStroke(30)"
                    [attr.stroke-dashoffset]="getDonutOffset(70)"
                    transform="rotate(-90 50 50)"
                  />
                </svg>
                <div class="donut-center">
                  <span class="donut-value"
                    >AED {{ getTotalCost() | number: "1.0-0" }}</span
                  >
                  <span class="donut-label">Total</span>
                </div>
              </div>
              <div class="chart-legend">
                <div class="legend-item">
                  <span class="legend-color" style="background: #3b82f6"></span>
                  <span>Platform Development (25%)</span>
                </div>
                <div class="legend-item">
                  <span class="legend-color" style="background: #8b5cf6"></span>
                  <span>Infrastructure (15%)</span>
                </div>
                <div class="legend-item">
                  <span class="legend-color" style="background: #06b6d4"></span>
                  <span>Integration (20%)</span>
                </div>
                <div class="legend-item">
                  <span class="legend-color" style="background: #10b981"></span>
                  <span>Operations (10%)</span>
                </div>
                <div class="legend-item">
                  <span class="legend-color" style="background: #f59e0b"></span>
                  <span>Contingency (30%)</span>
                </div>
              </div>
            </div>

            <!-- Revenue Projection Chart -->
            <div class="chart-card">
              <h4>📈 Revenue Projection</h4>
              <div class="bar-chart">
                <div
                  class="bar-group"
                  *ngFor="let month of getRevenueProjection()"
                >
                  <div class="bar" [style.height.%]="month.percentage"></div>
                  <span class="bar-label">{{ month.label }}</span>
                  <span class="bar-value"
                    >AED {{ month.value | number: "1.0-0" }}</span
                  >
                </div>
              </div>
            </div>
          </div>

          <!-- Implementation Timeline -->
          <div class="timeline-section">
            <h4>🗓️ Implementation Timeline</h4>
            <div class="timeline-track">
              <div
                class="timeline-phase"
                *ngFor="let phase of getImplementationPhases(); let i = index"
              >
                <div class="phase-marker">{{ i + 1 }}</div>
                <div class="phase-content">
                  <h5>{{ phase.name }}</h5>
                  <span class="phase-duration">{{ phase.duration }}</span>
                  <ul>
                    <li *ngFor="let activity of phase.activities">
                      {{ activity }}
                    </li>
                  </ul>
                </div>
              </div>
            </div>
          </div>

          <!-- Risk Assessment -->
          <div class="risk-section">
            <h4>⚠️ Risk Assessment</h4>
            <div class="risk-grid">
              <div class="risk-card" *ngFor="let risk of getRisks()">
                <div class="risk-header">
                  <span class="risk-name">{{ risk.name }}</span>
                  <span class="risk-level" [class]="risk.level.toLowerCase()">{{
                    risk.level
                  }}</span>
                </div>
                <p class="risk-description">{{ risk.description }}</p>
                <div class="risk-mitigation">
                  <strong>Mitigation:</strong> {{ risk.mitigation }}
                </div>
              </div>
            </div>
          </div>

          <!-- Export Actions -->
          <div class="export-section" *ngIf="proposal">
            <button class="btn-export primary" (click)="exportPdf()">
              📥 Download PDF Proposal
            </button>
            <button class="btn-export secondary" (click)="exportPowerPoint()">
              📊 Export PowerPoint
            </button>
            <button
              class="btn-export tertiary"
              (click)="copyExecutiveSummary()"
            >
              📋 Copy Summary
            </button>
          </div>
        </div>
      </div>
    </div>
  `,
  styles: [
    `
      .pilot-container {
        padding: 20px;
        max-width: 1400px;
        margin: 0 auto;
      }

      /* Header */
      .page-header {
        background: linear-gradient(135deg, #1e3a5f 0%, #0ea5e9 100%);
        border-radius: 16px;
        padding: 24px;
        margin-bottom: 24px;
      }

      .header-content {
        display: flex;
        align-items: center;
        gap: 16px;
      }

      .header-icon {
        font-size: 2.5rem;
        background: rgba(255, 255, 255, 0.15);
        width: 60px;
        height: 60px;
        display: flex;
        align-items: center;
        justify-content: center;
        border-radius: 12px;
      }

      .page-header h1 {
        margin: 0;
        color: white;
        font-size: 1.75rem;
      }

      .subtitle {
        margin: 4px 0 0;
        color: rgba(255, 255, 255, 0.9);
      }

      /* Grid */
      .content-grid {
        display: grid;
        grid-template-columns: 380px 1fr;
        gap: 24px;
      }

      @media (max-width: 1024px) {
        .content-grid {
          grid-template-columns: 1fr;
        }
      }

      /* Config Panel */
      .config-panel {
        position: sticky;
        top: 90px;
        height: fit-content;
      }

      .panel-card {
        background: white;
        border-radius: 16px;
        padding: 24px;
        box-shadow: 0 4px 16px rgba(0, 0, 0, 0.08);
      }

      .panel-card h3 {
        margin: 0 0 20px;
        color: #1e3a5f;
        font-size: 1.2rem;
      }

      .form-group {
        margin-bottom: 20px;
      }

      .form-group label {
        display: block;
        font-weight: 600;
        color: #334155;
        margin-bottom: 8px;
      }

      .form-select {
        width: 100%;
        padding: 12px;
        border: 2px solid #e2e8f0;
        border-radius: 10px;
        font-size: 14px;
      }

      /* Templates */
      .template-buttons {
        display: flex;
        flex-direction: column;
        gap: 10px;
        margin-bottom: 24px;
      }

      .template-btn {
        display: flex;
        align-items: center;
        gap: 12px;
        padding: 14px;
        border: 2px solid #e2e8f0;
        border-radius: 12px;
        background: white;
        cursor: pointer;
        transition: all 0.2s;
      }

      .template-btn:hover {
        border-color: #0ea5e9;
      }

      .template-btn.active {
        border-color: #0ea5e9;
        background: #f0f9ff;
      }

      .template-icon {
        font-size: 1.5rem;
      }

      .template-name {
        flex: 1;
        font-weight: 600;
        text-align: left;
      }

      .template-cost {
        color: #059669;
        font-weight: 600;
      }

      /* Options */
      .options-section h4 {
        margin: 0 0 16px;
        color: #334155;
      }

      .option-checkbox {
        display: flex;
        align-items: center;
        gap: 12px;
        padding: 12px;
        border-radius: 10px;
        margin-bottom: 10px;
        cursor: pointer;
        transition: background 0.2s;
      }

      .option-checkbox:hover {
        background: #f8fafc;
      }

      .option-checkbox input {
        display: none;
      }

      .checkbox-custom {
        width: 22px;
        height: 22px;
        border: 2px solid #cbd5e1;
        border-radius: 6px;
        display: flex;
        align-items: center;
        justify-content: center;
      }

      .option-checkbox input:checked + .checkbox-custom::after {
        content: "✓";
        color: white;
        font-size: 14px;
      }

      .option-checkbox input:checked + .checkbox-custom {
        background: #0ea5e9;
        border-color: #0ea5e9;
      }

      .option-content {
        flex: 1;
      }

      .option-content strong {
        display: block;
        color: #1e293b;
      }

      .option-content small {
        color: #64748b;
        font-size: 0.8rem;
      }

      .option-cost {
        color: #059669;
        font-weight: 600;
      }

      /* Range */
      .form-range {
        width: 100%;
        height: 8px;
        border-radius: 4px;
        background: #e2e8f0;
        -webkit-appearance: none;
      }

      .form-range::-webkit-slider-thumb {
        -webkit-appearance: none;
        width: 20px;
        height: 20px;
        border-radius: 50%;
        background: #0ea5e9;
        cursor: pointer;
      }

      .range-labels {
        display: flex;
        justify-content: space-between;
        margin-top: 8px;
        font-size: 0.75rem;
        color: #64748b;
      }

      .btn-generate {
        width: 100%;
        padding: 16px;
        background: linear-gradient(135deg, #0ea5e9 0%, #0284c7 100%);
        color: white;
        border: none;
        border-radius: 12px;
        font-size: 1rem;
        font-weight: 600;
        cursor: pointer;
        transition: transform 0.2s;
      }

      .btn-generate:hover:not(:disabled) {
        transform: translateY(-2px);
        box-shadow: 0 4px 12px rgba(14, 165, 233, 0.4);
      }

      .btn-generate:disabled {
        opacity: 0.6;
        cursor: not-allowed;
      }

      /* Results */
      .results-panel {
        display: flex;
        flex-direction: column;
        gap: 20px;
      }

      /* KPI Grid */
      .kpi-grid {
        display: grid;
        grid-template-columns: repeat(4, 1fr);
        gap: 16px;
      }

      @media (max-width: 768px) {
        .kpi-grid {
          grid-template-columns: repeat(2, 1fr);
        }
      }

      .kpi-card {
        background: white;
        border-radius: 14px;
        padding: 20px;
        display: flex;
        align-items: center;
        gap: 16px;
        box-shadow: 0 2px 8px rgba(0, 0, 0, 0.06);
      }

      .kpi-icon {
        font-size: 2rem;
      }

      .kpi-value {
        display: block;
        font-size: 1.25rem;
        font-weight: 700;
        color: #1e3a5f;
      }

      .kpi-label {
        font-size: 0.8rem;
        color: #64748b;
      }

      .kpi-card.primary {
        border-left: 4px solid #3b82f6;
      }
      .kpi-card.success {
        border-left: 4px solid #10b981;
      }
      .kpi-card.warning {
        border-left: 4px solid #f59e0b;
      }
      .kpi-card.info {
        border-left: 4px solid #8b5cf6;
      }

      /* Charts */
      .charts-grid {
        display: grid;
        grid-template-columns: 1fr 1fr;
        gap: 20px;
      }

      @media (max-width: 768px) {
        .charts-grid {
          grid-template-columns: 1fr;
        }
      }

      .chart-card {
        background: white;
        border-radius: 14px;
        padding: 24px;
        box-shadow: 0 2px 8px rgba(0, 0, 0, 0.06);
      }

      .chart-card h4 {
        margin: 0 0 20px;
        color: #1e3a5f;
      }

      /* Donut Chart */
      .donut-chart {
        position: relative;
        width: 180px;
        height: 180px;
        margin: 0 auto 20px;
      }

      .donut {
        transform: rotate(-90deg);
      }

      .donut-center {
        position: absolute;
        top: 50%;
        left: 50%;
        transform: translate(-50%, -50%);
        text-align: center;
      }

      .donut-value {
        display: block;
        font-size: 1rem;
        font-weight: 700;
        color: #1e3a5f;
      }

      .donut-label {
        font-size: 0.75rem;
        color: #64748b;
      }

      .chart-legend {
        display: flex;
        flex-direction: column;
        gap: 8px;
      }

      .legend-item {
        display: flex;
        align-items: center;
        gap: 8px;
        font-size: 0.85rem;
      }

      .legend-color {
        width: 12px;
        height: 12px;
        border-radius: 3px;
      }

      /* Bar Chart */
      .bar-chart {
        display: flex;
        align-items: flex-end;
        justify-content: space-around;
        height: 180px;
        padding-top: 20px;
      }

      .bar-group {
        display: flex;
        flex-direction: column;
        align-items: center;
        gap: 8px;
        flex: 1;
      }

      .bar {
        width: 40px;
        background: linear-gradient(180deg, #0ea5e9, #0284c7);
        border-radius: 6px 6px 0 0;
        min-height: 10px;
      }

      .bar-label {
        font-size: 0.7rem;
        color: #64748b;
      }

      .bar-value {
        font-size: 0.65rem;
        font-weight: 600;
        color: #1e3a5f;
      }

      /* Timeline */
      .timeline-section,
      .risk-section {
        background: white;
        border-radius: 14px;
        padding: 24px;
        box-shadow: 0 2px 8px rgba(0, 0, 0, 0.06);
      }

      .timeline-section h4,
      .risk-section h4 {
        margin: 0 0 20px;
        color: #1e3a5f;
      }

      .timeline-track {
        display: flex;
        gap: 16px;
        overflow-x: auto;
        padding-bottom: 10px;
      }

      .timeline-phase {
        flex: 1;
        min-width: 200px;
        position: relative;
      }

      .phase-marker {
        width: 32px;
        height: 32px;
        background: #0ea5e9;
        color: white;
        border-radius: 50%;
        display: flex;
        align-items: center;
        justify-content: center;
        font-weight: 700;
        margin-bottom: 12px;
      }

      .phase-content {
        background: #f8fafc;
        border-radius: 10px;
        padding: 16px;
      }

      .phase-content h5 {
        margin: 0 0 4px;
        color: #1e3a5f;
      }

      .phase-duration {
        font-size: 0.75rem;
        color: #0ea5e9;
        font-weight: 600;
      }

      .phase-content ul {
        margin: 8px 0 0;
        padding-left: 16px;
        font-size: 0.8rem;
        color: #475569;
      }

      /* Risk */
      .risk-grid {
        display: grid;
        grid-template-columns: repeat(2, 1fr);
        gap: 16px;
      }

      @media (max-width: 768px) {
        .risk-grid {
          grid-template-columns: 1fr;
        }
      }

      .risk-card {
        background: #f8fafc;
        border-radius: 10px;
        padding: 16px;
      }

      .risk-header {
        display: flex;
        justify-content: space-between;
        align-items: center;
        margin-bottom: 8px;
      }

      .risk-name {
        font-weight: 600;
        color: #1e293b;
      }

      .risk-level {
        font-size: 0.7rem;
        padding: 3px 8px;
        border-radius: 6px;
        font-weight: 600;
      }

      .risk-level.low {
        background: #d1fae5;
        color: #059669;
      }
      .risk-level.medium {
        background: #fef3c7;
        color: #b45309;
      }
      .risk-level.high {
        background: #fee2e2;
        color: #dc2626;
      }

      .risk-description {
        margin: 0 0 8px;
        font-size: 0.85rem;
        color: #475569;
      }

      .risk-mitigation {
        font-size: 0.8rem;
        color: #64748b;
      }

      /* Export */
      .export-section {
        display: flex;
        gap: 12px;
        flex-wrap: wrap;
      }

      .btn-export {
        padding: 14px 24px;
        border-radius: 10px;
        font-weight: 600;
        cursor: pointer;
        transition: all 0.2s;
        border: none;
      }

      .btn-export.primary {
        background: #0ea5e9;
        color: white;
      }

      .btn-export.primary:hover {
        background: #0284c7;
      }

      .btn-export.secondary {
        background: #f1f5f9;
        color: #475569;
        border: 1px solid #e2e8f0;
      }

      .btn-export.tertiary {
        background: white;
        color: #475569;
        border: 1px solid #e2e8f0;
      }
    `,
  ],
})
export class PilotProposalComponent implements OnInit {
  simulations: SimulationResult[] = [];
  selectedSimulationId = "";
  proposal: PilotProposalDto | null = null;
  error: string | null = null;
  isGenerating = false;
  selectedTemplate = "basic";

  options: PilotOptions = {
    includeUaePassIntegration: false,
    includeMobileApp: false,
    includeHardwareIntegration: false,
    include24x7Support: false,
    durationMonths: 6,
    customIntegrationCost: 0,
  };

  constructor(
    private pilotService: PilotProposalService,
    private simulationService: SimulationService,
  ) {}

  ngOnInit(): void {
    this.loadSimulations();
  }

  loadSimulations(): void {
    this.simulationService.getAllSimulations().subscribe({
      next: (data) => {
        this.simulations = data;
        if (data.length > 0) {
          this.selectedSimulationId = data[0].id;
        }
      },
      error: () => {
        this.error = "Failed to load simulations";
      },
    });
  }

  selectTemplate(template: string): void {
    this.selectedTemplate = template;
    if (template === "basic") {
      this.options = {
        includeUaePassIntegration: false,
        includeMobileApp: false,
        includeHardwareIntegration: false,
        include24x7Support: false,
        durationMonths: 6,
        customIntegrationCost: 0,
      };
    } else if (template === "standard") {
      this.options = {
        includeUaePassIntegration: true,
        includeMobileApp: true,
        includeHardwareIntegration: false,
        include24x7Support: true,
        durationMonths: 6,
        customIntegrationCost: 0,
      };
    } else {
      this.options = {
        includeUaePassIntegration: true,
        includeMobileApp: true,
        includeHardwareIntegration: true,
        include24x7Support: true,
        durationMonths: 9,
        customIntegrationCost: 0,
      };
    }
  }

  applyTemplate(template: "quick" | "full" | "enterprise"): void {
    if (template === "quick") this.selectTemplate("basic");
    else if (template === "full") this.selectTemplate("standard");
    else this.selectTemplate("enterprise");
  }

  generateProposal(): void {
    if (!this.selectedSimulationId) {
      this.error = "Select a simulation first";
      return;
    }

    this.isGenerating = true;
    this.error = null;

    this.pilotService
      .generateProposal(this.selectedSimulationId, this.options)
      .subscribe({
        next: (proposal) => {
          this.proposal = proposal;
          this.isGenerating = false;
        },
        error: () => {
          this.error = "Failed to generate proposal";
          this.isGenerating = false;
        },
      });
  }

  getSelectedSimulation(): SimulationResult | null {
    return (
      this.simulations.find((s) => s.id === this.selectedSimulationId) || null
    );
  }

  getTotalCost(): number {
    let total = 250000;
    if (this.options.includeUaePassIntegration) total += 50000;
    if (this.options.includeMobileApp) total += 75000;
    if (this.options.includeHardwareIntegration) total += 100000;
    if (this.options.include24x7Support) total += 60000;
    if (this.options.customIntegrationCost > 0)
      total += this.options.customIntegrationCost;
    return total;
  }

  getEstimatedAnnualRevenue(): number {
    const sim = this.getSelectedSimulation();
    if (!sim) return 0;
    return sim.estimatedRevenue * 365;
  }

  getEstimatedMonthlyRevenue(): number {
    const sim = this.getSelectedSimulation();
    if (!sim) return 0;
    return (sim.estimatedRevenue * 365) / 12;
  }

  getROI(): number {
    const cost = this.getTotalCost();
    const revenue = this.getEstimatedAnnualRevenue();
    if (cost === 0) return 0;
    return ((revenue - cost) / cost) * 100;
  }

  getDonutStroke(percentage: number): string {
    const circumference = 2 * Math.PI * 40;
    return `${(percentage / 100) * circumference} ${circumference}`;
  }

  getDonutOffset(offset: number): string {
    const circumference = 2 * Math.PI * 40;
    const offsetCircum = (offset / 100) * circumference;
    return `-${offsetCircum}`;
  }

  getRevenueProjection(): {
    label: string;
    value: number;
    percentage: number;
  }[] {
    const monthly = this.getEstimatedMonthlyRevenue();
    const months = ["M1", "M2", "M3", "M4", "M5", "M6"];
    const rampUp = [0.3, 0.5, 0.7, 0.85, 0.95, 1.0];
    const maxRevenue = monthly * 6;

    return months.map((label, i) => ({
      label,
      value: monthly * rampUp[i],
      percentage: ((monthly * rampUp[i]) / maxRevenue) * 100,
    }));
  }

  getImplementationPhases(): PilotPhase[] {
    const phases: PilotPhase[] = [
      {
        name: "Planning & Design",
        duration: "Weeks 1-4",
        activities: [
          "Requirements gathering",
          "System architecture",
          "UX design",
        ],
        deliverables: ["Design doc", "Architecture blueprint"],
      },
      {
        name: "Development",
        duration: "Weeks 5-12",
        activities: [
          "Backend development",
          "Frontend build",
          "API integration",
        ],
        deliverables: ["Core system", "Mobile app"],
      },
      {
        name: "Integration",
        duration: "Weeks 13-18",
        activities: ["Hardware setup", "Third-party integration", "Testing"],
        deliverables: ["Connected system", "Test reports"],
      },
      {
        name: "Pilot Launch",
        duration: "Weeks 19-24",
        activities: ["Go-live", "Monitoring", "Optimization"],
        deliverables: ["Live pilot", "KPI report"],
      },
    ];
    return phases;
  }

  getRisks(): {
    name: string;
    level: string;
    description: string;
    mitigation: string;
  }[] {
    return [
      {
        name: "Technical Complexity",
        level: "Medium",
        description: "Integration with existing toll systems",
        mitigation: "Phased integration approach",
      },
      {
        name: "Public Adoption",
        level: "High",
        description: "Low user registration and usage",
        mitigation: "Marketing campaign & incentives",
      },
      {
        name: "Regulatory Approval",
        level: "Medium",
        description: "Delays in government sign-off",
        mitigation: "Early stakeholder engagement",
      },
      {
        name: "Operational Issues",
        level: "Low",
        description: "System downtime or errors",
        mitigation: "24/7 support & SLA",
      },
    ];
  }

  exportPdf(): void {
    window.print();
  }

  exportPowerPoint(): void {
    alert("PowerPoint export would generate a .pptx file");
  }

  copyExecutiveSummary(): void {
    if (!this.proposal?.executiveSummary) return;
    navigator.clipboard.writeText(this.proposal.executiveSummary);
    alert("Executive summary copied to clipboard!");
  }
}
