import { Component, OnInit } from "@angular/core";
import { SimulationService } from "../../services/simulation.service";
import { SimulationResult } from "../../models/simulation.model";
import { jsPDF } from "jspdf";

interface CashFlowPoint {
  x: number;
  y: number;
}

@Component({
  selector: "app-business-case",
  templateUrl: "./business-case.component.html",
  styleUrls: ["./business-case.component.css"],
})
export class BusinessCaseComponent implements OnInit {
  simulations: SimulationResult[] = [];
  selectedSimulationId = "";
  error: string | null = null;

  pilotCost = 250000;
  growthRate = 8;
  discountRate = 10;
  years = 5;

  cashFlows: number[] = [];
  npv = 0;
  irr = 0;
  paybackPeriod = -1;

  constructor(private simulationService: SimulationService) {}

  ngOnInit(): void {
    this.loadSimulations();
    this.recalculate();
  }

  loadSimulations(): void {
    this.simulationService.getAllSimulations().subscribe({
      next: (data) => {
        this.simulations = data;
        if (data.length > 0) {
          this.selectedSimulationId = data[0].id;
          this.recalculate();
        }
      },
      error: () => {
        this.error = "Failed to load simulations";
      },
    });
  }

  onInputsChanged(): void {
    this.recalculate();
  }

  getSelectedSimulation(): SimulationResult | null {
    return (
      this.simulations.find((s) => s.id === this.selectedSimulationId) || null
    );
  }

  recalculate(): void {
    const simulation = this.getSelectedSimulation();
    if (!simulation) {
      this.cashFlows = [];
      return;
    }

    const annualRevenue = simulation.estimatedRevenue * 365;
    const maintenance = annualRevenue * 0.15;
    const operational = annualRevenue * 0.1;
    const growth = this.growthRate / 100;

    let cumulative = -this.pilotCost;
    const flows = [cumulative];

    for (let year = 1; year <= this.years; year += 1) {
      const yearRevenue = annualRevenue * Math.pow(1 + growth, year - 1);
      const yearNet = yearRevenue - maintenance - operational;
      cumulative += yearNet;
      flows.push(cumulative);
    }

    this.cashFlows = flows;
    this.npv = this.calculateNPV(flows, this.discountRate / 100);
    this.irr = this.calculateIRR(flows);
    this.paybackPeriod = this.calculatePaybackPeriod(flows);
  }

  getCashFlowPoints(width = 320, height = 140): string {
    if (this.cashFlows.length === 0) return "";
    const max = Math.max(...this.cashFlows);
    const min = Math.min(...this.cashFlows);
    const range = max - min || 1;

    const points: CashFlowPoint[] = this.cashFlows.map((value, index) => {
      const x = (index / (this.cashFlows.length - 1)) * width;
      const y = height - ((value - min) / range) * height;
      return { x, y };
    });

    return points.map((p) => `${p.x},${p.y}`).join(" ");
  }

  getNPVGauge(): string {
    const normalized = Math.max(0, Math.min(100, this.npv / 1000000));
    return `${normalized}%`;
  }

  getSensitivityBars(): { label: string; value: number }[] {
    const base = this.npv;
    const plusGrowth = this.calculateScenario(2, 0, 0);
    const minusGrowth = this.calculateScenario(-2, 0, 0);
    const plusDiscount = this.calculateScenario(0, 2, 0);
    const minusDiscount = this.calculateScenario(0, -2, 0);
    const plusCost = this.calculateScenario(0, 0, 50000);
    const minusCost = this.calculateScenario(0, 0, -50000);

    return [
      { label: "Growth +2%", value: plusGrowth - base },
      { label: "Growth -2%", value: minusGrowth - base },
      { label: "Discount +2%", value: plusDiscount - base },
      { label: "Discount -2%", value: minusDiscount - base },
      { label: "Cost +50k", value: plusCost - base },
      { label: "Cost -50k", value: minusCost - base },
    ];
  }

  getTornadoWidth(value: number): number {
    const normalized = Math.min(100, (Math.abs(value) / 500000) * 100);
    return normalized;
  }

  getSroiValue(): number {
    const socialBenefit =
      this.getProductivityGainValue() +
      this.getCo2Value() +
      this.getHealthcareReductionValue();
    if (this.pilotCost === 0) return 0;
    return socialBenefit / this.pilotCost;
  }

  getCo2Value(): number {
    const simulation = this.getSelectedSimulation();
    if (!simulation) return 0;
    const tons = simulation.environmentalImpact / 1000;
    return tons * 250;
  }

  getProductivityGainValue(): number {
    const simulation = this.getSelectedSimulation();
    if (!simulation) return 0;
    const avgWage = 45;
    const hoursSaved = (simulation.congestionReduction / 100) * 0.5;
    const dailyValue = simulation.totalVehicles * hoursSaved * avgWage;
    return dailyValue * 365;
  }

  getHealthcareReductionValue(): number {
    const simulation = this.getSelectedSimulation();
    if (!simulation) return 0;
    const tons = simulation.environmentalImpact / 1000;
    return tons * 120;
  }

  exportInvestmentMemo(): void {
    const sim = this.getSelectedSimulation();
    if (!sim) return;

    const doc = new jsPDF();
    doc.setFontSize(16);
    doc.text("Smart Zone Investment Memo", 14, 20);

    doc.setFontSize(12);
    doc.text("Executive Summary", 14, 32);
    doc.text(
      `Projected daily revenue: AED ${sim.estimatedRevenue.toFixed(0)}. ROI: ${this.irr.toFixed(1)}%.`,
      14,
      40,
    );

    doc.text("Financial Projections", 14, 56);
    doc.text(`Pilot Cost: AED ${this.pilotCost.toFixed(0)}`, 14, 64);
    doc.text(`NPV: AED ${this.npv.toFixed(0)}`, 14, 72);
    doc.text(`Payback: Year ${this.paybackPeriod}`, 14, 80);

    doc.text("Risk Assessment", 14, 96);
    doc.text("- Regulatory alignment and public acceptance", 14, 104);
    doc.text("- Data integration readiness", 14, 112);

    doc.text("Recommendation", 14, 128);
    doc.text("Proceed with pilot and monitor KPIs quarterly.", 14, 136);

    doc.save("investment-memo.pdf");
  }

  private calculateScenario(
    growthDelta: number,
    discountDelta: number,
    costDelta: number,
  ): number {
    const simulation = this.getSelectedSimulation();
    if (!simulation) return 0;

    const annualRevenue = simulation.estimatedRevenue * 365;
    const maintenance = annualRevenue * 0.15;
    const operational = annualRevenue * 0.1;
    const growth = (this.growthRate + growthDelta) / 100;
    const pilotCost = this.pilotCost + costDelta;
    const discount = (this.discountRate + discountDelta) / 100;

    let cumulative = -pilotCost;
    const flows = [cumulative];

    for (let year = 1; year <= this.years; year += 1) {
      const yearRevenue = annualRevenue * Math.pow(1 + growth, year - 1);
      const yearNet = yearRevenue - maintenance - operational;
      cumulative += yearNet;
      flows.push(cumulative);
    }

    return this.calculateNPV(flows, discount);
  }

  private calculateNPV(cashFlows: number[], discountRate: number): number {
    return cashFlows.reduce((acc, flow, index) => {
      return acc + flow / Math.pow(1 + discountRate, index);
    }, 0);
  }

  private calculateIRR(cashFlows: number[]): number {
    let guess = 0.1;
    for (let i = 0; i < 50; i += 1) {
      let npv = 0;
      let derivative = 0;
      for (let t = 0; t < cashFlows.length; t += 1) {
        npv += cashFlows[t] / Math.pow(1 + guess, t);
        if (t > 0) {
          derivative -= (t * cashFlows[t]) / Math.pow(1 + guess, t + 1);
        }
      }
      if (Math.abs(derivative) < 1e-6) break;
      const next = guess - npv / derivative;
      if (!isFinite(next)) break;
      if (Math.abs(next - guess) < 1e-6) {
        guess = next;
        break;
      }
      guess = next;
    }
    return guess * 100;
  }

  private calculatePaybackPeriod(cashFlows: number[]): number {
    for (let i = 0; i < cashFlows.length; i += 1) {
      if (cashFlows[i] >= 0) return i;
    }
    return -1;
  }
}
<div class="business-container">
  <div class="business-header">
    <h2>Business Case Calculator</h2>
    <p>Interactive financial model for UAE smart mobility pilots.</p>
  </div>

  <div class="business-grid">
    <div class="business-form">
      <label>Simulation</label>
      <select [(ngModel)]="selectedSimulationId" (change)="onInputsChanged()">
        <option value="">Select simulation</option>
        <option *ngFor="let sim of simulations" [value]="sim.id">
          {{ sim.name }} • AED {{ sim.estimatedRevenue | number }} / day
        </option>
      </select>

      <label>Pilot Cost (AED)</label>
      <input
        type="range"
        min="100000"
        max="500000"
        step="10000"
        [(ngModel)]="pilotCost"
        (input)="onInputsChanged()"
      />
      <div class="slider-value">AED {{ pilotCost | number }}</div>

      <label>Annual Growth Rate</label>
      <input
        type="range"
        min="5"
        max="15"
        step="1"
        [(ngModel)]="growthRate"
        (input)="onInputsChanged()"
      />
      <div class="slider-value">{{ growthRate }}%</div>

      <label>Discount Rate</label>
      <input
        type="range"
        min="8"
        max="12"
        step="0.5"
        [(ngModel)]="discountRate"
        (input)="onInputsChanged()"
      />
      <div class="slider-value">{{ discountRate }}%</div>

      <label>Projection Years</label>
      <select [(ngModel)]="years" (change)="onInputsChanged()">
        <option [value]="3">3 years</option>
        <option [value]="5">5 years</option>
        <option [value]="10">10 years</option>
      </select>

      <button class="btn-export" (click)="exportInvestmentMemo()">
        Export Investment Memo PDF
      </button>

      <div class="error" *ngIf="error">{{ error }}</div>
    </div>

    <div class="business-visuals">
      <div class="card">
        <h4>Cash Flow (Cumulative)</h4>
        <svg viewBox="0 0 320 140">
          <polyline
            [attr.points]="getCashFlowPoints()"
            fill="none"
            stroke="#2196F3"
            stroke-width="3"
          ></polyline>
        </svg>
      </div>

      <div class="card gauges">
        <div>
          <h4>NPV</h4>
          [style.width.%]="getTornadoWidth(bar.value)"
        </div>
        <div>
          <h4>IRR</h4>
          <div class="gauge">
            <div class="gauge-fill" [style.width]="getNPVGauge()"></div>
          </div>
          <p>{{ irr | number: "1.0-1" }}%</p>
        </div>
        <div>
          <h4>Payback</h4>
          <p *ngIf="paybackPeriod >= 0">Year {{ paybackPeriod }}</p>
          <p *ngIf="paybackPeriod < 0">Beyond horizon</p>
        </div>
      </div>

      <div class="card">
        <h4>Sensitivity Tornado</h4>
        <div class="tornado">
          <div class="tornado-row" *ngFor="let bar of getSensitivityBars()">
            <span>{{ bar.label }}</span>
            <div class="tornado-bar">
              <div
                class="fill"
                [style.width.%]="getTornadoWidth(bar.value)"
                [class.negative]="bar.value < 0"
              ></div>
            </div>
            <strong>{{ bar.value | number: "1.0-0" }}</strong>
          </div>
        </div>
      </div>

      <div class="card">
        <h4>UAE Government Metrics</h4>
        <div class="gov-metrics">
          <div>
            <span>SROI</span>
            <strong>{{ getSroiValue() | number: "1.2-2" }}x</strong>
          </div>
          <div>
            <span>CO₂ Value</span>
            <strong>AED {{ getCo2Value() | number: "1.0-0" }}</strong>
          </div>
          <div>
            <span>Productivity Gains</span>
            <strong
              >AED {{ getProductivityGainValue() | number: "1.0-0" }}</strong
            >
          </div>
          <div>
            <span>Healthcare Reduction</span>
            <strong
              >AED {{ getHealthcareReductionValue() | number: "1.0-0" }}</strong
            >
          </div>
        </div>
      </div>
    </div>
  </div>
</div>
