export class ScenarioComparisonComponent {
  constructor(simulationService, chartService, ngZone, reportService) {
    this.simulationService = simulationService;
    this.chartService = chartService;
    this.ngZone = ngZone;
    this.reportService = reportService;
    this.availableScenarios = [{
      id: "1",
      name: "Baseline",
      description: "No pricing (current state)",
      color: "#94a3b8"
    }, {
      id: "2",
      name: "Light Pricing",
      description: "AED 10/hr, 2hr free",
      color: "#10b981"
    }, {
      id: "3",
      name: "Moderate",
      description: "AED 25/hr, 1hr free",
      color: "#3b82f6"
    }, {
      id: "4",
      name: "Aggressive",
      description: "AED 50/hr, no free hours",
      color: "#ef4444"
    }];
    this.selectedScenarios = [];
    this.isRunning = false;
    this.showCustomForm = false;
    this.customScenario = {
      name: "",
      description: "",
      rate: 20,
      freeHours: 1,
      multiplier: 1.5,
      color: "#8b5cf6"
    };
    this.revenueWinner = null;
    this.envWinner = null;
    this.equityWinner = null;
    this.recommendation = "";
  }
  ngOnInit() {}
  get hasResults() {
    return this.selectedScenarios.length >= 2 && this.selectedScenarios.every(s => s.result);
  }
  isSelected(scenario) {
    return this.selectedScenarios.some(s => s.id === scenario.id);
  }
  toggleScenario(scenario) {
    if (this.isSelected(scenario)) {
      this.selectedScenarios = this.selectedScenarios.filter(s => s.id !== scenario.id);
    } else {
      if (this.selectedScenarios.length < 4) {
        this.selectedScenarios.push({
          ...scenario
        });
      }
    }
  }
  addCustomScenario() {
    const newScenario = {
      id: `custom-${Date.now()}`,
      name: this.customScenario.name,
      description: this.customScenario.description || `AED ${this.customScenario.rate}/hr`,
      color: this.customScenario.color
    };
    this.availableScenarios.push(newScenario);
    this.showCustomForm = false;
    this.customScenario = {
      name: "",
      description: "",
      rate: 20,
      freeHours: 1,
      multiplier: 1.5,
      color: "#8b5cf6"
    };
  }
  runAllScenarios() {
    if (this.selectedScenarios.length < 2) return;
    this.isRunning = true;
    // Run simulation for each selected scenario
    this.selectedScenarios.forEach((scenario, index) => {
      this.runSimulationForScenario(scenario, index);
    });
  }
  runSimulationForScenario(scenario, index) {
    // Get rate from scenario name/description
    let rate = 25;
    let freeHours = 1;
    if (scenario.name.includes("Light")) {
      rate = 10;
      freeHours = 2;
    } else if (scenario.name.includes("Moderate")) {
      rate = 25;
      freeHours = 1;
    } else if (scenario.name.includes("Aggressive")) {
      rate = 50;
      freeHours = 0;
    } else if (scenario.name === "Baseline") {
      rate = 0;
      freeHours = 0;
    }
    // Create mock results for demo
    const mockResult = {
      id: scenario.id,
      name: scenario.name,
      description: scenario.description,
      totalVehicles: 15000 + index * 1000,
      vehiclesDiverted: Math.floor(1500 * (rate / 25) * (index + 1) * 0.3),
      congestionReduction: 5 + rate / 10 * (index + 1),
      estimatedRevenue: rate * 800 * (index + 1),
      equityImpact: {
        lowIncomeBurden: 1.5 + index * 0.5,
        highIncomeBurden: 1 + index * 0.2,
        burdenRatio: 1.5 + index * 0.3,
        isRegressive: rate > 40
      },
      environmentalImpact: 3000 + index * 800,
      zoneRules: {},
      results: {},
      createdAt: new Date()
    };
    scenario.result = mockResult;
    // Check if all done
    if (this.selectedScenarios.every(s => s.result)) {
      this.isRunning = false;
      this.analyzeResults();
      this.renderCharts();
    }
  }
  analyzeResults() {
    // Find winners
    this.revenueWinner = [...this.selectedScenarios].filter(s => s.result).sort((a, b) => (b.result?.estimatedRevenue || 0) - (a.result?.estimatedRevenue || 0))[0];
    this.envWinner = [...this.selectedScenarios].filter(s => s.result).sort((a, b) => (b.result?.environmentalImpact || 0) - (a.result?.environmentalImpact || 0))[0];
    this.equityWinner = [...this.selectedScenarios].filter(s => s.result && !s.result.equityImpact?.isRegressive).sort((a, b) => (a.result?.equityImpact?.burdenRatio || 0) - (b.result?.equityImpact?.burdenRatio || 0))[0] || this.selectedScenarios[0];
    // Generate recommendation
    if (this.revenueWinner && this.envWinner && this.equityWinner) {
      if (this.revenueWinner.name === this.envWinner.name) {
        this.recommendation = `${this.revenueWinner.name} offers the best balance of revenue and environmental impact.`;
      } else {
        this.recommendation = `Consider ${this.revenueWinner.name} for revenue or ${this.envWinner.name} for environmental goals. ${this.equityWinner.name} provides the most equitable outcome.`;
      }
    }
  }
  renderCharts() {
    const labels = this.selectedScenarios.map(s => s.name);
    const congestionData = this.selectedScenarios.map(s => s.result?.congestionReduction || 0);
    const revenueData = this.selectedScenarios.map(s => s.result?.estimatedRevenue || 0);
    const colors = this.selectedScenarios.map(s => s.color);
    // Use NgZone to run after Angular change detection
    this.ngZone.runOutsideAngular(() => {
      setTimeout(() => {
        this.chartService.destroyAllCharts();
        // Create charts with retry logic
        this.createChartWithRetry("compareCongestionChart", () => this.chartService.createBarChart("compareCongestionChart", labels, [{
          label: "Congestion Reduction %",
          data: congestionData,
          backgroundColor: colors
        }]));
        this.createChartWithRetry("compareRevenueChart", () => this.chartService.createBarChart("compareRevenueChart", labels, [{
          label: "Revenue (AED)",
          data: revenueData,
          backgroundColor: colors
        }]));
        this.createChartWithRetry("compareMultiChart", () => this.chartService.createBarChart("compareMultiChart", labels, [{
          label: "Congestion Reduction",
          data: congestionData,
          backgroundColor: colors[0] || "#3b82f6"
        }, {
          label: "Revenue (√∑1000)",
          data: revenueData.map(v => v / 1000),
          backgroundColor: colors[1] || "#10b981"
        }]));
      }, 500);
    });
  }
  createChartWithRetry(canvasId, createChartFn, retryCount = 0) {
    const maxRetries = 10;
    const canvas = document.getElementById(canvasId);
    if (canvas) {
      createChartFn();
    } else if (retryCount < maxRetries) {
      setTimeout(() => {
        this.createChartWithRetry(canvasId, createChartFn, retryCount + 1);
      }, 200);
    } else {
      console.warn(`Canvas element '${canvasId}' not found after ${maxRetries} retries`);
    }
  }
  exportComparison() {
    if (!this.hasResults) {
      alert("Please run simulations first before exporting.");
      return;
    }
    // Generate comparison report
    const scenariosData = this.selectedScenarios.map(s => s.result).filter(r => r !== undefined);
    // Create a summary report
    const reportHtml = `
