      }

      /* Custom Form */
      .custom-form {
        margin-top: 20px;
        padding: 20px;
        background: #f8fafc;
        border-radius: 12px;
      }

      .custom-form h4 {
        margin: 0 0 16px;
        color: #1e3a5f;
      }

      .form-grid {
        display: grid;
        grid-template-columns: repeat(3, 1fr);
        gap: 16px;
        margin-bottom: 16px;
      }

      .form-group label {
        display: block;
        font-size: 13px;
        color: #64748b;
        margin-bottom: 6px;
      }

      .form-group input {
        width: 100%;
        padding: 10px;
        border: 1px solid #e2e8f0;
        border-radius: 8px;
      }

      .create-btn {
        padding: 10px 20px;
        background: #10b981;
        color: white;
        border: none;
        border-radius: 8px;
        cursor: pointer;
      }

      /* Results Section */
      .results-section {
        background: white;
        border-radius: 16px;
        padding: 24px;
        box-shadow: 0 4px 16px rgba(0, 0, 0, 0.08);
      }

      .summary-section h3,
      .winner-section h3 {
        margin: 0 0 16px;
        color: #1e3a5f;
      }

      /* Metrics Compare */
      .metrics-compare {
        border: 1px solid #e2e8f0;
        border-radius: 12px;
        overflow: hidden;
