      }

      .metric-header,
      .metric-row {
        display: grid;
        grid-template-columns: 200px repeat(auto-fit, minmax(120px, 1fr));
        padding: 12px 16px;
      }

      .metric-header {
        background: #f8fafc;
        font-weight: 600;
