  }
  serializeDeckState() {
    const state = {
      agency: this.selectedAgency,
      customAgency: this.customAgency,
      logoDataUrl: this.logoDataUrl,
      theme: this.theme,
      slides: this.slides
    };
    return btoa(encodeURIComponent(JSON.stringify(state)));
  }
  applyDeckState(encodedState) {
    try {
      const decoded = decodeURIComponent(atob(encodedState));
      const state = JSON.parse(decoded);
      this.selectedAgency = state.agency || this.selectedAgency;
      this.customAgency = state.customAgency || "";
      this.logoDataUrl = state.logoDataUrl || null;
      this.theme = state.theme || this.theme;
      this.slides = state.slides && state.slides.length ? state.slides : this.slides;
      this.selectedSlideIndex = 0;
    } catch (error) {
      console.error("Invalid deck state", error);
    }
  }
  setupRemoteChannel() {
    if (this.channel) return;
    if ("BroadcastChannel" in window) {
      this.channel = new BroadcastChannel(`pitch-deck-${this.sessionId}`);
      this.channel.onmessage = event => this.handleRemoteMessage(event.data);
    }
  }
  generateSessionId() {
    if (typeof crypto !== "undefined" && "randomUUID" in crypto) {
      return crypto.randomUUID();
    }
    return Math.random().toString(36).slice(2, 10);
