import {
  Component,
  ElementRef,
  OnDestroy,
  OnInit,
  ViewChild,
} from "@angular/core";
import { ActivatedRoute, Router } from "@angular/router";

interface PitchSlide {
  id: string;
  title: string;
  content: string;
  notes: string;
}

interface DeckTheme {
  primary: string;
  secondary: string;
  accent: string;
  background: string;
  text: string;
}

@Component({
  selector: "app-pitch-deck",
  templateUrl: "./pitch-deck.component.html",
  styleUrls: ["./pitch-deck.component.css"],
})
export class PitchDeckComponent implements OnInit, OnDestroy {
  @ViewChild("presentationRoot") presentationRoot?: ElementRef<HTMLDivElement>;

  agencyOptions = [
    "Abu Dhabi ITC",
    "Dubai RTA",
    "Smart Dubai",
    "Ministry of Energy & Infrastructure",
    "Custom",
  ];

  selectedAgency = "Abu Dhabi ITC";
  customAgency = "";
  logoDataUrl: string | null = null;

  theme: DeckTheme = {
    primary: "#00732F",
    secondary: "#FFFFFF",
    accent: "#CE1126",
    background: "#F8F9FB",
    text: "#111111",
  };

  slides: PitchSlide[] = [];
  selectedSlideIndex = 0;

  isPresenting = false;
  showSpeakerNotes = true;
  presentationIndex = 0;
  timerSeconds = 0;
  private timerId: number | null = null;

  shareableLink = "";
  embedCode = "";
  remoteLink = "";

  isRemoteControl = false;
  private channel: BroadcastChannel | null = null;
  private sessionId = "";
  private keydownHandler = (event: KeyboardEvent) => this.handleKeydown(event);

  constructor(
    private route: ActivatedRoute,
    private router: Router,
  ) {
    this.sessionId = this.generateSessionId();
    this.applyTemplate("quick");
  }

  ngOnInit(): void {
    this.route.queryParams.subscribe((params) => {
      const deckParam = params["deck"];
      const remoteParam = params["remote"];
      const sessionParam = params["session"];
      const authorityParam = params["authority"];

      // Handle authority parameter from authority-selection
      if (authorityParam) {
        this.selectedAgency = this.getAgencyFromAuthority(authorityParam);
        this.applyTemplate("government");
      }

      if (sessionParam) {
        this.sessionId = sessionParam;
      }

      if (deckParam) {
        this.applyDeckState(deckParam);
      }

      if (remoteParam === "1") {
        this.isRemoteControl = true;
      }

      this.setupRemoteChannel();
      this.updateLinks();
    });
  }

  private getAgencyFromAuthority(authority: string): string {
    const mapping: Record<string, string> = {
      RTA: "Dubai RTA",
      ITC: "Abu Dhabi ITC",
      SRTA: "Sharjah SRTA",
    };
    return mapping[authority.toUpperCase()] || "Dubai RTA";
  }

  ngOnDestroy(): void {
    this.stopTimer();
    window.removeEventListener("keydown", this.keydownHandler);
    if (this.channel) {
      this.channel.close();
      this.channel = null;
    }
  }

  get selectedSlide(): PitchSlide {
    return this.slides[this.selectedSlideIndex];
  }

  get agencyName(): string {
    return this.selectedAgency === "Custom" && this.customAgency
      ? this.customAgency
      : this.selectedAgency;
  }

  applyTemplate(
    template: "quick" | "investor" | "government" | "technical",
  ): void {
    const templates: Record<string, PitchSlide[]> = {
      quick: [
        this.createSlide("Vision", "Smart mobility pricing for UAE cities."),
        this.createSlide("Problem", "Congestion, emissions, and lost time."),
        this.createSlide("Solution", "Dynamic smart zone access model."),
        this.createSlide("Impact", "Revenue, equity, and emission metrics."),
        this.createSlide("Next Steps", "Pilot, scale, and governance setup."),
      ],
      investor: [
        this.createSlide("Vision", "Modern smart mobility pricing."),
        this.createSlide("Market", "UAE urban growth & transport demand."),
        this.createSlide("Problem", "Congestion costs, delays, emissions."),
        this.createSlide("Solution", "Dynamic pricing + incentives."),
        this.createSlide(
          "Product",
          "Zone pricing, exemption logic, analytics.",
        ),
        this.createSlide("Traction", "Simulation results and pilots."),
        this.createSlide("Business Model", "Fees, compliance, data services."),
        this.createSlide("Financials", "Projected ROI and payback."),
        this.createSlide("Team", "Mobility + data expertise."),
        this.createSlide("Ask", "Pilot funding and regulatory support."),
      ],
      government: [
        this.createSlide("Executive Summary", "Government-ready pilot plan."),
        this.createSlide(
          "Policy Objectives",
          "Reduce congestion, improve equity.",
        ),
        this.createSlide(
          "Current Challenges",
          "Peak-hour overload & emissions.",
        ),
        this.createSlide("Solution Overview", "Smart pricing zones."),
        this.createSlide("Zone Design", "Geofenced compliance & exemptions."),
        this.createSlide("Technology", "Analytics, enforcement, integrations."),
        this.createSlide(
          "Data Governance",
          "Privacy, security, data residency.",
        ),
        this.createSlide("Economic Impact", "Revenue + social ROI."),
        this.createSlide("Equity & Inclusion", "Tiered pricing + subsidies."),
        this.createSlide("Implementation", "Phased rollout plan."),
        this.createSlide("KPIs", "Traffic, revenue, CO₂, satisfaction."),
        this.createSlide("Risk Mitigation", "Public engagement, pilots."),
        this.createSlide("Budget", "CAPEX/OPEX breakdown."),
        this.createSlide("Timeline", "Pilot to scale (12-18 months)."),
        this.createSlide("Decision Request", "Approve pilot and funding."),
      ],
      technical: [
        this.createSlide("Overview", "Technical architecture for smart zones."),
        this.createSlide("System Components", "Apps, enforcement, analytics."),
        this.createSlide("Data Sources", "Traffic sensors, permits, GPS."),
        this.createSlide("Ingestion Pipeline", "Streaming + batch analytics."),
        this.createSlide("Pricing Engine", "Rules, exemptions, ML pricing."),
        this.createSlide("Simulation Model", "Agent-based + calibration."),
        this.createSlide("Geofencing", "Polygon zones + edge detection."),
        this.createSlide("Identity & Access", "UAE Pass + RBAC."),
        this.createSlide("Payments", "Wallets, toll integrations."),
        this.createSlide("Security", "Encryption, audit logging."),
        this.createSlide("Observability", "Dashboards + alerts."),
        this.createSlide("Reliability", "SLA & failover."),
        this.createSlide("Data Privacy", "Residency & anonymization."),
        this.createSlide("APIs", "Partner integrations."),
        this.createSlide("Deployment", "Cloud + on-prem hybrid."),
        this.createSlide("Scalability", "Multi-city expansion."),
        this.createSlide("Performance", "Latency benchmarks."),
        this.createSlide("Testing", "Simulation + load testing."),
        this.createSlide("Roadmap", "Phase 1-3 rollout."),
        this.createSlide("Appendix", "Technical documentation."),
      ],
    };

    this.slides = templates[template].map((slide, index) => ({
      ...slide,
      id: `${template}-${index}-${this.generateSessionId()}`,
    }));
    this.selectedSlideIndex = 0;
    this.updateLinks();
  }

  createSlide(title: string, content: string): PitchSlide {
    return {
      id: this.generateSessionId(),
      title,
      content,
      notes: "",
    };
  }

  selectSlide(index: number): void {
    this.selectedSlideIndex = index;
  }

  addSlide(): void {
    const newSlide = this.createSlide(
      `New Slide ${this.slides.length + 1}`,
      "Add slide content here.",
    );
    this.slides.splice(this.selectedSlideIndex + 1, 0, newSlide);
    this.selectedSlideIndex += 1;
    this.updateLinks();
  }

  removeSlide(index: number): void {
    if (this.slides.length <= 1) return;
    this.slides.splice(index, 1);
    this.selectedSlideIndex = Math.min(
      Math.max(0, this.selectedSlideIndex - 1),
      this.slides.length - 1,
    );
    this.updateLinks();
  }

  onLogoUpload(event: Event): void {
    const input = event.target as HTMLInputElement;
    const file = input.files?.[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = () => {
      this.logoDataUrl = reader.result as string;
      this.updateLinks();
    };
    reader.readAsDataURL(file);
  }

  clearLogo(): void {
    this.logoDataUrl = null;
    this.updateLinks();
  }

  startPresentation(): void {
    this.isPresenting = true;
    this.presentationIndex = this.selectedSlideIndex;
    this.timerSeconds = 0;
    this.startTimer();
    window.addEventListener("keydown", this.keydownHandler);

    if (this.presentationRoot?.nativeElement.requestFullscreen) {
      this.presentationRoot.nativeElement.requestFullscreen().catch(() => null);
    }
  }

  stopPresentation(): void {
    this.isPresenting = false;
    this.stopTimer();
    window.removeEventListener("keydown", this.keydownHandler);

    if (document.fullscreenElement) {
      document.exitFullscreen().catch(() => null);
    }
  }

  nextSlide(): void {
    if (this.presentationIndex < this.slides.length - 1) {
      this.presentationIndex += 1;
    }
  }

  prevSlide(): void {
    if (this.presentationIndex > 0) {
      this.presentationIndex -= 1;
    }
  }

  toggleSpeakerNotes(): void {
    this.showSpeakerNotes = !this.showSpeakerNotes;
  }

  getFormattedTimer(): string {
    const minutes = Math.floor(this.timerSeconds / 60)
      .toString()
      .padStart(2, "0");
    const seconds = (this.timerSeconds % 60).toString().padStart(2, "0");
    return `${minutes}:${seconds}`;
  }

  private startTimer(): void {
    this.stopTimer();
    this.timerId = window.setInterval(() => {
      this.timerSeconds += 1;
    }, 1000);
  }

  private stopTimer(): void {
    if (this.timerId) {
      window.clearInterval(this.timerId);
      this.timerId = null;
    }
  }

  async exportPptx(): Promise<void> {
    const deckElement = document.getElementById("pitch-deck-export");
    if (!deckElement) return;

    try {
      const htmlToPptx = (window as any).htmlToPptx;
      if (!htmlToPptx) {
        console.error("htmlToPptx not loaded");
        return;
      }
      await htmlToPptx(deckElement, {
        filename: "pitch-deck.pptx",
      });
    } catch (error) {
      console.error("PPTX export failed", error);
    }
  }

  exportPdf(): void {
    window.print();
  }

  generateShareableLink(): void {
    this.updateLinks();
  }

  copyShareableLink(): void {
    if (!this.shareableLink) return;
    navigator.clipboard.writeText(this.shareableLink).catch(() => null);
  }

  copyRemoteLink(): void {
    if (!this.remoteLink) return;
    navigator.clipboard.writeText(this.remoteLink).catch(() => null);
  }

  copyEmbedCode(): void {
    if (!this.embedCode) return;
    navigator.clipboard.writeText(this.embedCode).catch(() => null);
  }

  openRemoteLink(): void {
    if (!this.remoteLink) return;
    window.open(this.remoteLink, "_blank");
  }

  sendRemoteCommand(command: string): void {
    if (!this.channel) return;
    this.channel.postMessage({ command });
  }

  private handleRemoteMessage(data: any): void {
    if (!data?.command) return;
    switch (data.command) {
      case "next":
        this.nextSlide();
        break;
      case "prev":
        this.prevSlide();
        break;
      case "toggleNotes":
        this.toggleSpeakerNotes();
        break;
      case "stop":
        this.stopPresentation();
        break;
      default:
        break;
    }
  }

  private handleKeydown(event: KeyboardEvent): void {
    if (!this.isPresenting) return;
    if (event.key === "ArrowRight" || event.key === " ") {
      this.nextSlide();
    }
    if (event.key === "ArrowLeft") {
      this.prevSlide();
    }
    if (event.key === "Escape") {
      this.stopPresentation();
    }
  }

  updateLinks(): void {
    const deckState = this.serializeDeckState();
    const urlTree = this.router.createUrlTree(["/pitch-deck"], {
      queryParams: { deck: deckState },
    });
    this.shareableLink = `${window.location.origin}${this.router.serializeUrl(urlTree)}`;
    this.embedCode = `<iframe src="${this.shareableLink}" width="100%" height="720" style="border:0;" allowfullscreen></iframe>`;

    const remoteTree = this.router.createUrlTree(["/pitch-deck"], {
      queryParams: { remote: 1, session: this.sessionId },
    });
    this.remoteLink = `${window.location.origin}${this.router.serializeUrl(remoteTree)}`;
  }

  private serializeDeckState(): string {
    const state = {
      agency: this.selectedAgency,
      customAgency: this.customAgency,
      logoDataUrl: this.logoDataUrl,
      theme: this.theme,
      slides: this.slides,
    };

    return btoa(encodeURIComponent(JSON.stringify(state)));
  }

  private applyDeckState(encodedState: string): void {
    try {
      const decoded = decodeURIComponent(atob(encodedState));
      const state = JSON.parse(decoded) as {
        agency: string;
        customAgency: string;
        logoDataUrl: string | null;
        theme: DeckTheme;
        slides: PitchSlide[];
      };

      this.selectedAgency = state.agency || this.selectedAgency;
      this.customAgency = state.customAgency || "";
      this.logoDataUrl = state.logoDataUrl || null;
      this.theme = state.theme || this.theme;
      this.slides =
        state.slides && state.slides.length ? state.slides : this.slides;
      this.selectedSlideIndex = 0;
    } catch (error) {
      console.error("Invalid deck state", error);
    }
  }

  private setupRemoteChannel(): void {
    if (this.channel) return;
    if ("BroadcastChannel" in window) {
      this.channel = new BroadcastChannel(`pitch-deck-${this.sessionId}`);
      this.channel.onmessage = (event) => this.handleRemoteMessage(event.data);
    }
  }

  private generateSessionId(): string {
    if (typeof crypto !== "undefined" && "randomUUID" in crypto) {
      return crypto.randomUUID();
    }
    return Math.random().toString(36).slice(2, 10);
  }
}
<div
  class="pitch-deck"
  [style.--deck-primary]="theme.primary"
  [style.--deck-secondary]="theme.secondary"
  [style.--deck-accent]="theme.accent"
  [style.--deck-bg]="theme.background"
  [style.--deck-text]="theme.text"
>
  <div class="pitch-header">
    <h2>Pitch Deck Generator</h2>
    <p>Craft, customize, and present agency-ready decks in minutes.</p>
  </div>

  <div *ngIf="isRemoteControl" class="remote-control">
    <h3>Remote Control</h3>
    <p>Use these controls to drive the presentation.</p>
    <div class="remote-buttons">
      <button (click)="sendRemoteCommand('prev')">Previous</button>
      <button (click)="sendRemoteCommand('next')">Next</button>
      <button (click)="sendRemoteCommand('toggleNotes')">Toggle Notes</button>
      <button class="danger" (click)="sendRemoteCommand('stop')">
        End Presentation
      </button>
    </div>
  </div>

  <div *ngIf="!isRemoteControl" class="pitch-grid">
    <div class="pitch-form">
      <h3>Agency & Branding</h3>
      <label>Agency</label>
      <select [(ngModel)]="selectedAgency" (change)="updateLinks()">
        <option *ngFor="let agency of agencyOptions" [value]="agency">
          {{ agency }}
        </option>
      </select>

      <div *ngIf="selectedAgency === 'Custom'">
        <label>Custom Agency Name</label>
        <input type="text" [(ngModel)]="customAgency" (input)="updateLinks()" />
      </div>

      <label>Agency Logo</label>
      <input type="file" accept="image/*" (change)="onLogoUpload($event)" />
      <button class="text-button" (click)="clearLogo()" *ngIf="logoDataUrl">
        Remove Logo
      </button>

      <div class="theme-panel">
        <h4>Color Scheme (UAE)</h4>
        <div class="theme-grid">
          <div>
            <span>Primary</span>
            <input
              type="color"
              [(ngModel)]="theme.primary"
              (input)="updateLinks()"
            />
          </div>
          <div>
            <span>Secondary</span>
            <input
              type="color"
              [(ngModel)]="theme.secondary"
              (input)="updateLinks()"
            />
          </div>
          <div>
            <span>Accent</span>
            <input
              type="color"
              [(ngModel)]="theme.accent"
              (input)="updateLinks()"
            />
          </div>
          <div>
            <span>Background</span>
            <input
              type="color"
              [(ngModel)]="theme.background"
              (input)="updateLinks()"
            />
          </div>
          <div>
            <span>Text</span>
            <input
              type="color"
              [(ngModel)]="theme.text"
              (input)="updateLinks()"
            />
          </div>
        </div>
      </div>

      <h3>Templates</h3>
      <div class="template-buttons">
        <button (click)="applyTemplate('quick')">Quick Pitch (5)</button>
        <button (click)="applyTemplate('investor')">Investor Deck (10)</button>
        <button (click)="applyTemplate('government')">
          Government Proposal (15)
        </button>
        <button (click)="applyTemplate('technical')">
          Technical Deep Dive (20)
        </button>
      </div>

      <h3>Slides</h3>
      <div class="slide-list">
        <div
          *ngFor="let slide of slides; let i = index"
          class="slide-item"
          [class.active]="i === selectedSlideIndex"
          (click)="selectSlide(i)"
        >
          <span>{{ i + 1 }}. {{ slide.title }}</span>
          <button
            class="icon-button"
            (click)="removeSlide(i); $event.stopPropagation()"
          >
            ✕
          </button>
        </div>
      </div>
      <button class="btn-secondary" (click)="addSlide()">Add Slide</button>

      <h3>Slide Editor</h3>
      <label>Title</label>
      <input
        type="text"
        [(ngModel)]="selectedSlide.title"
        (input)="updateLinks()"
      />

      <label>Content</label>
      <textarea
        rows="6"
        [(ngModel)]="selectedSlide.content"
        (input)="updateLinks()"
      ></textarea>

      <label>Speaker Notes</label>
      <textarea
        rows="4"
        [(ngModel)]="selectedSlide.notes"
        (input)="updateLinks()"
      ></textarea>
    </div>

    <div class="pitch-preview">
      <div class="preview-toolbar">
        <div class="toolbar-left">
          <button (click)="startPresentation()">Start Presentation</button>
          <button (click)="toggleSpeakerNotes()">
            {{ showSpeakerNotes ? "Hide" : "Show" }} Notes
          </button>
          <button (click)="openRemoteLink()">Open Remote Control</button>
        </div>
        <div class="toolbar-right">
          <span>Timer: {{ getFormattedTimer() }}</span>
        </div>
      </div>

      <div class="deck-preview" #presentationRoot>
        <div class="deck-header">
          <div>
            <h4>{{ agencyName }}</h4>
            <p>Smart Zone Pitch Deck</p>
          </div>
          <img *ngIf="logoDataUrl" [src]="logoDataUrl" alt="Agency logo" />
        </div>

        <div class="slide-preview">
          <h2>{{ selectedSlide.title }}</h2>
          <pre>{{ selectedSlide.content }}</pre>
        </div>

        <div class="slide-footer">
          <span>{{ selectedSlideIndex + 1 }} / {{ slides.length }}</span>
          <span>Prepared for {{ agencyName }}</span>
        </div>
      </div>

      <div id="pitch-deck-export" class="deck-export">
        <div class="export-slide" *ngFor="let slide of slides; let i = index">
          <div class="deck-header">
            <div>
              <h4>{{ agencyName }}</h4>
              <p>Smart Zone Pitch Deck</p>
            </div>
            <img *ngIf="logoDataUrl" [src]="logoDataUrl" alt="Agency logo" />
          </div>

          <div class="slide-preview">
            <h2>{{ slide.title }}</h2>
            <pre>{{ slide.content }}</pre>
          </div>

          <div class="slide-footer">
            <span>{{ i + 1 }} / {{ slides.length }}</span>
            <span>Prepared for {{ agencyName }}</span>
          </div>
        </div>
      </div>

      <div *ngIf="showSpeakerNotes" class="speaker-notes">
        <h4>Speaker Notes</h4>
        <p>{{ selectedSlide.notes || "Add notes to guide the presenter." }}</p>
      </div>

      <div class="export-panel">
        <h3>Export & Sharing</h3>
        <div class="export-actions">
          <button (click)="exportPptx()">Export PPTX</button>
          <button (click)="exportPdf()">Export PDF (Print)</button>
          <button (click)="generateShareableLink()">Generate Share Link</button>
        </div>

        <label>Shareable Link</label>
        <div class="copy-row">
          <input type="text" [value]="shareableLink" readonly />
          <button (click)="copyShareableLink()">Copy</button>
        </div>

        <label>Embed Code</label>
        <div class="copy-row">
          <textarea rows="3" readonly>{{ embedCode }}</textarea>
          <button (click)="copyEmbedCode()">Copy</button>
        </div>

        <label>Remote Control Link</label>
        <div class="copy-row">
          <input type="text" [value]="remoteLink" readonly />
          <button (click)="copyRemoteLink()">Copy</button>
        </div>
      </div>
    </div>
  </div>

  <div class="presentation-overlay" *ngIf="isPresenting">
    <div
      class="presentation"
      [style.--deck-primary]="theme.primary"
      [style.--deck-secondary]="theme.secondary"
      [style.--deck-accent]="theme.accent"
      [style.--deck-bg]="theme.background"
      [style.--deck-text]="theme.text"
    >
      <div class="presentation-header">
        <span>{{ agencyName }}</span>
        <span>{{ getFormattedTimer() }}</span>
      </div>

      <div class="presentation-slide">
        <h1>{{ slides[presentationIndex].title }}</h1>
        <pre>{{ slides[presentationIndex].content }}</pre>
      </div>

      <div class="presentation-footer">
        <button (click)="prevSlide()">Prev</button>
        <button (click)="nextSlide()">Next</button>
        <button class="danger" (click)="stopPresentation()">Exit</button>
      </div>

      <div class="presentation-notes" *ngIf="showSpeakerNotes">
        <h4>Speaker Notes</h4>
        <p>{{ slides[presentationIndex].notes || "No notes added." }}</p>
      </div>
    </div>
  </div>
</div>
