import { Component, OnInit } from "@angular/core";
import { forkJoin } from "rxjs";
import { AuthorityDataService } from "../services/authority-data.service";
import {
  AuthorityDataSource,
  AuthorityZoneRecommendation,
} from "../models/authority-data.model";

interface AuthorityKpi {
  name: string;
  target: string;
  importance: number;
}

@Component({
  selector: "app-authority-selection",
  template: `
    <div class="authority-selection">
      <h2>ðŸ‡¦ðŸ‡ª Select Transport Authority</h2>
      <p class="subtitle">
        Get authority-specific analysis and recommendations
      </p>

      <div class="authority-cards">
        <!-- RTA Dubai Card -->
        <div
          class="authority-card"
          [class.selected]="selectedAuthority === 'RTA'"
          (click)="selectAuthority('RTA')"
        >
          <div class="authority-header" style="border-color: #CE1126;">
            <img
              src="/assets/images/rta-dubai.jpg"
              alt="Dubai RTA logo"
              class="authority-logo"
            />
            <div class="authority-info">
              <h3 style="color: #CE1126;">Dubai RTA</h3>
              <span class="authority-jurisdiction">Dubai Emirate</span>
            </div>
          </div>
          <div class="authority-stats">
            <div class="stat">
              <div class="stat-value">4.2B</div>
              <div class="stat-label">AED congestion cost/year</div>
            </div>
            <div class="stat">
              <div class="stat-value">1.8M</div>
              <div class="stat-label">Registered vehicles</div>
            </div>
            <div class="stat">
              <div class="stat-value">17.5%</div>
              <div class="stat-label">Public transport share</div>
            </div>
          </div>
          <div class="authority-focus">
            <strong>Focus Areas:</strong>
            <ul>
              <li>Tourist areas congestion</li>
              <li>Metro integration</li>
              <li>Smart city initiatives</li>
              <li>Dubai 2040 plan</li>
            </ul>
          </div>
          <button class="select-button" *ngIf="selectedAuthority === 'RTA'">
            âœ“ Selected for Analysis
          </button>
        </div>

        <!-- ITC Abu Dhabi Card -->
        <div
          class="authority-card"
          [class.selected]="selectedAuthority === 'ITC'"
          (click)="selectAuthority('ITC')"
        >
          <div class="authority-header" style="border-color: #000000;">
            <img
              src="/assets/images/itc.png"
              alt="Abu Dhabi ITC logo"
              class="authority-logo"
            />
            <div class="authority-info">
              <h3 style="color: #000000;">Abu Dhabi ITC</h3>
              <span class="authority-jurisdiction">Abu Dhabi Emirate</span>
            </div>
          </div>
          <div class="authority-stats">
            <div class="stat">
              <div class="stat-value">2.8B</div>
              <div class="stat-label">AED congestion cost/year</div>
            </div>
            <div class="stat">
              <div class="stat-value">1.2M</div>
              <div class="stat-label">Registered vehicles</div>
            </div>
            <div class="stat">
              <div class="stat-value">12%</div>
              <div class="stat-label">Public transport share</div>
            </div>
          </div>
          <div class="authority-focus">
            <strong>Focus Areas:</strong>
            <ul>
              <li>Industrial traffic (Mussafah)</li>
              <li>Economic development</li>
              <li>Safety improvement</li>
              <li>Net Zero 2050</li>
            </ul>
          </div>
          <button class="select-button" *ngIf="selectedAuthority === 'ITC'">
            âœ“ Selected for Analysis
          </button>
        </div>

        <!-- SRTA Sharjah Card -->
        <div
          class="authority-card"
          [class.selected]="selectedAuthority === 'SRTA'"
          (click)="selectAuthority('SRTA')"
        >
          <div class="authority-header" style="border-color: #003366;">
            <img
              src="/assets/images/srta.png"
              alt="Sharjah SRTA logo"
              class="authority-logo"
            />
            <div class="authority-info">
              <h3 style="color: #003366;">Sharjah SRTA</h3>
              <span class="authority-jurisdiction">Sharjah Emirate</span>
            </div>
          </div>
          <div class="authority-stats">
            <div class="stat">
              <div class="stat-value">1.5B</div>
              <div class="stat-label">AED congestion cost/year</div>
            </div>
            <div class="stat">
              <div class="stat-value">0.5M</div>
              <div class="stat-label">Registered vehicles</div>
            </div>
            <div class="stat">
              <div class="stat-value">8%</div>
              <div class="stat-label">Public transport share</div>
            </div>
          </div>
          <div class="authority-focus">
            <strong>Focus Areas:</strong>
            <ul>
              <li>Sharjah-Dubai commuters</li>
              <li>Affordable housing access</li>
              <li>Industrial worker mobility</li>
              <li>Border crossing efficiency</li>
            </ul>
          </div>
          <button class="select-button" *ngIf="selectedAuthority === 'SRTA'">
            âœ“ Selected for Analysis
          </button>
        </div>
      </div>

      <!-- Authority-Specific Analysis -->
      <div class="authority-analysis" *ngIf="selectedAuthority">
        <h3>Analysis for {{ getAuthorityName(selectedAuthority) }}</h3>

        <!-- Zone Recommendations -->
        <div class="recommendations">
          <h4>Recommended Pilot Zones</h4>
          <div class="zone-recommendations">
            <div
              class="zone-card"
              *ngFor="let zone of getRecommendedZones(selectedAuthority)"
            >
              <h5>{{ zone.name }}</h5>
              <div class="zone-stats">
                <span>Peak volume: {{ zone.peakVolume }} vehicles/hour</span>
                <span>Congestion: {{ zone.congestion }}%</span>
                <span>Authority priority: {{ zone.priority }}/10</span>
              </div>
              <button (click)="selectZoneForSimulation(zone)">
                Analyze This Zone
              </button>
            </div>
          </div>
        </div>

        <!-- Authority KPIs -->
        <div class="authority-kpis">
          <h4>Authority-Specific KPIs</h4>
          <div class="kpi-grid">
            <div
              class="kpi-card"
              *ngFor="let kpi of getAuthorityKPIs(selectedAuthority)"
            >
              <div class="kpi-name">{{ kpi.name }}</div>
              <div class="kpi-target">Target: {{ kpi.target }}</div>
              <div class="kpi-importance">
                Priority: {{ kpi.importance }}/10
              </div>
            </div>
          </div>
        </div>

        <!-- Authority Data Sources -->
        <div class="data-sources">
          <h4>Available Data Sources</h4>
          <ul>
            <li *ngFor="let source of getDataSources(selectedAuthority)">
              <a [href]="source.url" target="_blank">{{ source.name }}</a>
              <span class="data-quality">Quality: {{ source.quality }}/10</span>
            </li>
          </ul>
        </div>

        <!-- Generate Authority Report -->
        <div class="generate-report">
          <button class="primary-btn" (click)="generateAuthorityReport()">
            Generate {{ selectedAuthority }} Report
          </button>
          <button class="secondary-btn" (click)="generatePitchDeck()">
            Create {{ selectedAuthority }} Pitch Deck
          </button>
        </div>
      </div>
    </div>
  `,
  styles: [
    `
      .authority-cards {
        display: grid;
        grid-template-columns: repeat(auto-fit, minmax(350px, 1fr));
        gap: 20px;
        margin: 30px 0;
      }

      .authority-card {
        border: 2px solid #ddd;
        border-radius: 10px;
        padding: 20px;
        cursor: pointer;
        transition: all 0.3s ease;
      }

      .authority-card.selected {
        border-color: currentColor;
        box-shadow: 0 5px 20px rgba(0, 0, 0, 0.1);
        transform: translateY(-5px);
      }

      .authority-header {
        display: flex;
        align-items: center;
        padding-bottom: 15px;
        border-bottom: 3px solid;
        margin-bottom: 15px;
      }

      .authority-logo {
        width: 60px;
        height: 60px;
        margin-right: 15px;
        object-fit: contain;
      }

      .authority-stats {
        display: grid;
        grid-template-columns: repeat(3, 1fr);
        gap: 10px;
        margin: 15px 0;
      }

      .stat-value {
        font-size: 24px;
        font-weight: bold;
        color: #ce1126;
      }

      .stat-label {
        font-size: 12px;
        color: #666;
      }
    `,
  ],
})
export class AuthoritySelectionComponent implements OnInit {
  selectedAuthority = "";
  isLoading = false;
  private authorityDataSources: Record<string, AuthorityDataSource[]> = {};
  private authorityRecommendations: Record<
    string,
    AuthorityZoneRecommendation[]
  > = {};

  constructor(private authorityDataService: AuthorityDataService) {}

  ngOnInit(): void {
    this.prefetchAuthorityData();
  }

  selectAuthority(authority: string): void {
    this.selectedAuthority = authority;
    this.loadAuthorityData(authority);
  }

  getAuthorityName(authority: string): string {
    const names: Record<string, string> = {
      RTA: "Dubai RTA",
      ITC: "Abu Dhabi ITC",
      SRTA: "Sharjah SRTA",
    };

    return names[authority] ?? authority;
  }

  getRecommendedZones(authority: string): AuthorityZoneRecommendation[] {
    const cached = this.authorityRecommendations[authority];
    if (cached?.length) {
      return cached;
    }

    const recommendations: Record<string, AuthorityZoneRecommendation[]> = {
      RTA: [
        { name: "Dubai Marina", peakVolume: 3800, congestion: 85, priority: 9 },
        { name: "Business Bay", peakVolume: 4200, congestion: 80, priority: 8 },
        {
          name: "Deira Commercial",
          peakVolume: 4500,
          congestion: 90,
          priority: 7,
        },
      ],
      ITC: [
        {
          name: "Mussafah Industrial",
          peakVolume: 4200,
          congestion: 90,
          priority: 10,
        },
        { name: "Khalifa City", peakVolume: 2800, congestion: 75, priority: 8 },
        {
          name: "Abu Dhabi City Center",
          peakVolume: 3500,
          congestion: 80,
          priority: 7,
        },
      ],
      SRTA: [
        {
          name: "Sharjah Industrial Area",
          peakVolume: 3200,
          congestion: 85,
          priority: 9,
        },
        {
          name: "Al Taawun Border",
          peakVolume: 4800,
          congestion: 95,
          priority: 10,
        },
        {
          name: "University City",
          peakVolume: 2200,
          congestion: 70,
          priority: 7,
        },
      ],
    };

    return recommendations[authority] ?? [];
  }

  getAuthorityKPIs(authority: string): AuthorityKpi[] {
    const kpis: Record<string, AuthorityKpi[]> = {
      RTA: [
        {
          name: "Tourist district congestion",
          target: "Reduce by 15%",
          importance: 9,
        },
        { name: "Metro ridership", target: "Increase by 10%", importance: 8 },
        {
          name: "Smart city score",
          target: "Increase by 5 points",
          importance: 7,
        },
      ],
      ITC: [
        {
          name: "Industrial corridor delay",
          target: "Reduce by 12%",
          importance: 10,
        },
        { name: "Safety incidents", target: "Reduce by 8%", importance: 9 },
        { name: "Net Zero 2050", target: "Cut CO2 by 10%", importance: 8 },
      ],
      SRTA: [
        {
          name: "Border crossing time",
          target: "Reduce by 20%",
          importance: 10,
        },
        {
          name: "Affordable housing access",
          target: "Increase by 12%",
          importance: 8,
        },
        {
          name: "Industrial commute cost",
          target: "Reduce by 5%",
          importance: 7,
        },
      ],
    };

    return kpis[authority] ?? [];
  }

  getDataSources(authority: string): AuthorityDataSource[] {
    const cached = this.authorityDataSources[authority];
    if (cached?.length) {
      return cached;
    }

    const sources: Record<string, AuthorityDataSource[]> = {
      RTA: [
        {
          name: "RTA Open Data Portal",
          url: "https://www.rta.ae/wps/portal/rta/ae/home/about-rta/open-data",
          quality: 9,
        },
        { name: "Dubai Data", url: "https://www.dubaidata.gov.ae", quality: 8 },
      ],
      ITC: [
        {
          name: "Abu Dhabi Open Data",
          url: "https://addata.gov.ae",
          quality: 7,
        },
        { name: "ITC Reports", url: "https://www.itc.gov.ae", quality: 8 },
      ],
      SRTA: [
        {
          name: "SRTA Data Portal",
          url: "https://data.srta.gov.ae",
          quality: 6,
        },
        {
          name: "Sharjah Statistics Center",
          url: "https://www.sharjahstatistics.gov.ae",
          quality: 7,
        },
      ],
    };

    return sources[authority] ?? [];
  }

  selectZoneForSimulation(zone: AuthorityZoneRecommendation): void {
    console.log("Selected zone for analysis", zone);
  }

  generateAuthorityReport(): void {
    console.log("Generating authority report for", this.selectedAuthority);
  }

  generatePitchDeck(): void {
    console.log("Generating pitch deck for", this.selectedAuthority);
  }

  private loadAuthorityData(authority: string): void {
    this.isLoading = true;
    forkJoin({
      sources: this.authorityDataService.getDataSources(authority),
      recommendations: this.authorityDataService.getRecommendations(authority),
    }).subscribe({
      next: ({ sources, recommendations }) => {
        this.authorityDataSources[authority] = sources;
        this.authorityRecommendations[authority] = recommendations;
      },
      error: () => null,
      complete: () => {
        this.isLoading = false;
      },
    });
  }

  private prefetchAuthorityData(): void {
    ["RTA", "ITC", "SRTA"].forEach((authority) =>
      this.loadAuthorityData(authority),
    );
  }
}
