-items: center;\n        padding: 16px;\n        background: #f8fafc;\n        border-radius: 12px;\n        margin-bottom: 8px;\n        border: 2px solid transparent;\n      }\n\n      .payment-card.selected[_ngcontent-%COMP%] {\n        border-color: #3b82f6;\n        background: #eff6ff;\n      }\n\n      .card-icon[_ngcontent-%COMP%] {\n        font-size: 24px;\n        margin-right: 12px;\n      }\n\n      .card-number[_ngcontent-%COMP%] {\n        flex: 1;\n        font-weight: 600;\n      }\n\n      .card-default[_ngcontent-%COMP%] {\n        font-size: 11px;\n        background: #10b981;\n        color: white;\n        padding: 2px 8px;\n        border-radius: 4px;\n      }\n\n      .add-card-btn[_ngcontent-%COMP%] {\n        width: 100%;\n        padding: 12px;\n        background: white;\n        border: 2px dashed #e2e8f0;\n        border-radius: 12px;\n        color: #64748b;\n        cursor: pointer;\n      }\n\n      .auto-recharge[_ngcontent-%COMP%] {\n        margin-top: 16px;\n        padding: 12px;\n        background: #f8fafc;\n        border-radius: 8px;\n      }\n\n      .month-selector[_ngcontent-%COMP%] {\n        display: flex;\n        justify-content: space-between;\n        align-items: center;\n        margin-bottom: 16px;\n      }\n\n      .month-btn[_ngcontent-%COMP%] {\n        background: none;\n        border: none;\n        font-size: 18px;\n        cursor: pointer;\n      }\n\n      .history-summary[_ngcontent-%COMP%] {\n        display: flex;\n        gap: 12px;\n      }\n\n      .summary-item[_ngcontent-%COMP%] {\n        flex: 1;\n        background: #f8fafc;\n        padding: 12px;\n        border-radius: 8px;\n        text-align: center;\n      }\n\n      .summary-item[_ngcontent-%COMP%]   .label[_ngcontent-%COMP%] {\n        display: block;\n        font-size: 11px;\n        color: #64748b;\n      }\n\n      .summary-item[_ngcontent-%COMP%]   .value[_ngcontent-%COMP%] {\n        display: block;\n        font-weight: 700;\n  